import React, { useState, useEffect, useRef } from 'react';
import { Ghost, Users, Copy, Settings, Phone, PhoneOff, Image, Send, QrCode, Sparkles, Crown, Shield, Star } from 'lucide-react';
import Message from '../components/Message';
import UserList from '../components/UserList';
import SettingsModal from '../components/SettingsModal';
import QRCodeModal from '../components/QRCodeModal';
import CallRoom from '../components/CallRoom';
import { THEMES } from '../constants';

const Chat = ({
  socket,
  username,
  roomId,
  roomName,
  messages,
  setMessages,
  users,
  setUsers,
  isAdmin,
  setIsAdmin,
  isSeniorMod,
  setIsSeniorMod,
  isModerator,
  setIsModerator,
  roomSettings,
  setRoomSettings,
  bannedUsers,
  setBannedUsers,
  userJoinTime,
  setCurrentView,
  callState,
  setCallState,
  encrypt,
  resetState
}) => {
  const [message, setMessage] = useState('');
  const [showSettings, setShowSettings] = useState(false);
  const [showQRCode, setShowQRCode] = useState(false);
  const [cooldown, setCooldown] = useState(0);
  const [joinCooldown, setJoinCooldown] = useState(0);
  const [isInCall, setIsInCall] = useState(false);
  const [isCallMinimized, setIsCallMinimized] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);
  const fileInputRef = useRef(null);
  const currentTheme = THEMES[roomSettings.theme] || THEMES.default;

  useEffect(() => {
    socket.on('cooldownSet', (cooldown) => {
      setCooldown(cooldown);
    });

    socket.on('joinCooldownSet', (cooldown) => {
      setJoinCooldown(cooldown);
    });

    socket.on('callStarted', (newCallState) => {
      setCallState(newCallState);
    });

    socket.on('callEnded', () => {
      setCallState(null);
      setIsInCall(false);
    });

    return () => {
      socket.off('cooldownSet');
      socket.off('joinCooldownSet');
      socket.off('callStarted');
      socket.off('callEnded');
    };
  }, [socket, setCallState]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    if (cooldown > 0) {
      const timer = setInterval(() => {
        setCooldown(prev => {
          if (prev <= 0) {
            clearInterval(timer);
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [cooldown]);

  useEffect(() => {
    if (joinCooldown > 0 && userJoinTime) {
      const timer = setInterval(() => {
        const timePassed = Math.floor((Date.now() - userJoinTime) / 1000);
        const remaining = Math.max(0, joinCooldown - timePassed);
        if (remaining <= 0) {
          clearInterval(timer);
        }
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [joinCooldown, userJoinTime]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file && file.type.startsWith('image/')) {
      sendImage(file);
    }
  };

  const sendMessage = (e) => {
    e.preventDefault();
    if (!message.trim()) return;
    
    const isPrivileged = isAdmin || isSeniorMod || isModerator;
    
    if (!isPrivileged) {
      const timePassed = userJoinTime ? Math.floor((Date.now() - userJoinTime) / 1000) : 0;
      if (joinCooldown > 0 && timePassed < joinCooldown) {
        const remaining = joinCooldown - timePassed;
        setMessages(prev => [...prev, {
          id: Date.now(),
          type: 'system',
          content: `You must wait ${remaining}s before speaking...`,
          timestamp: new Date().toISOString()
        }]);
        return;
      }

      if (cooldown > 0) {
        setMessages(prev => [...prev, {
          id: Date.now(),
          type: 'system',
          content: `You must wait ${cooldown}s before sending another message...`,
          timestamp: new Date().toISOString()
        }]);
        return;
      }

      const containsLink = /https?:\/\/[^\s]+/.test(message);
      if (!roomSettings.allowLinks && containsLink) {
        setMessages(prev => [...prev, {
          id: Date.now(),
          type: 'system',
          content: 'Links are disabled in this realm...',
          timestamp: new Date().toISOString()
        }]);
        return;
      }
    }

    const messageData = {
      id: Date.now(),
      username,
      content: message.trim(),
      timestamp: new Date().toISOString(),
      type: 'message'
    };

    const encryptedMessage = encrypt(messageData);
    socket.emit('message', { roomId, message: encryptedMessage });
    setMessage('');
    setIsTyping(false);
    if (!isPrivileged) {
      setCooldown(roomSettings.cooldown);
    }
  };

  const sendImage = (file) => {
    if (!roomSettings.allowImages && !(isAdmin || isSeniorMod || isModerator)) {
      setMessages(prev => [...prev, {
        id: Date.now(),
        type: 'system',
        content: 'Image sharing is disabled in this realm...',
        timestamp: new Date().toISOString()
      }]);
      return;
    }

    const reader = new FileReader();
    reader.onload = (e) => {
      const messageData = {
        id: Date.now(),
        username,
        content: e.target.result,
        timestamp: new Date().toISOString(),
        type: 'image'
      };

      const encryptedMessage = encrypt(messageData);
      socket.emit('message', { roomId, message: encryptedMessage });
      if (!(isAdmin || isSeniorMod || isModerator)) {
        setCooldown(roomSettings.cooldown);
      }
    };
    reader.readAsDataURL(file);
  };

  const deleteMessage = (messageId) => {
    socket.emit('deleteMessage', { roomId, messageId });
  };

  const copyRoomLink = () => {
    const link = `${window.location.origin}?room=${roomId}`;
    navigator.clipboard.writeText(link);
    setMessages(prev => [...prev, {
      id: Date.now(),
      type: 'system',
      content: 'Room link copied to clipboard!',
      timestamp: new Date().toISOString()
    }]);
  };

  const startCall = () => {
    socket.emit('startCall', { roomId });
    setIsInCall(true);
    setIsCallMinimized(false);
  };

  const joinCall = () => {
    socket.emit('joinCall', { roomId });
    setIsInCall(true);
    setIsCallMinimized(false);
  };

  const minimizeCall = () => {
    setIsCallMinimized(true);
  };

  const getRoleIcon = () => {
    if (isAdmin) return <Crown className="w-4 h-4 text-yellow-400" />;
    if (isSeniorMod) return <Shield className="w-4 h-4 text-purple-400" />;
    if (isModerator) return <Star className="w-4 h-4 text-blue-400" />;
    return null;
  };

  const timePassed = userJoinTime ? Math.floor((Date.now() - userJoinTime) / 1000) : 0;
  const joinCooldownRemaining = Math.max(0, joinCooldown - timePassed);

  return (
    <div className={`h-screen ${currentTheme.bg} flex flex-col transition-all duration-500 relative overflow-hidden`}>
      {/* Animated Background */}
      <div className="absolute inset-0 opacity-20">
        <div className="absolute top-0 left-0 w-96 h-96 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl animate-pulse-slow"></div>
        <div className="absolute top-0 right-0 w-96 h-96 bg-blue-500 rounded-full mix-blend-multiply filter blur-xl animate-pulse-slow animation-delay-1000"></div>
        <div className="absolute bottom-0 left-0 w-96 h-96 bg-pink-500 rounded-full mix-blend-multiply filter blur-xl animate-pulse-slow animation-delay-2000"></div>
      </div>

      {/* Header */}
      <div className={`${currentTheme.card} border-b ${currentTheme.border} p-4 shadow-2xl backdrop-blur-xl bg-opacity-80 relative z-10`}>
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <div className="relative">
              <Ghost className="w-10 h-10 text-purple-400 animate-pulse-slow drop-shadow-lg" />
              <div className="absolute -top-1 -right-1 w-4 h-4 bg-green-500 rounded-full animate-ping"></div>
            </div>
            <div>
              <div className="flex items-center space-x-2">
                <h1 className="text-2xl font-bold bg-gradient-to-r from-purple-400 via-pink-400 to-blue-400 bg-clip-text text-transparent tracking-tight">
                  {roomName || 'Unnamed Realm'}
                </h1>
                {getRoleIcon()}
              </div>
              <div className="flex items-center space-x-2">
                <p className={`text-xs ${currentTheme.textSecondary} font-mono`}>
                  ID: {roomId}
                </p>
                <div className={`w-2 h-2 rounded-full ${users.length > 0 ? 'bg-green-500' : 'bg-gray-500'} animate-pulse`}></div>
              </div>
            </div>
          </div>
          
          <div className="flex items-center space-x-2">
            <div className={`${currentTheme.buttonSecondary} rounded-2xl px-4 py-3 flex items-center space-x-2 backdrop-blur-md bg-opacity-80 border border-purple-500/20`}>
              <Users className="w-5 h-5 text-purple-400" />
              <span className={`text-sm font-semibold ${currentTheme.textPrimary}`}>{users.length}</span>
              <div className="w-2 h-2 bg-purple-400 rounded-full animate-pulse"></div>
            </div>
            
            <button
              onClick={copyRoomLink}
              className={`${currentTheme.buttonSecondary} rounded-2xl px-4 py-3 flex items-center space-x-2 ${currentTheme.hover} transition-all duration-300 transform hover:scale-105 backdrop-blur-md bg-opacity-80 border border-purple-500/20 group`}
              title="Copy Room Link"
            >
              <Copy className="w-5 h-5 group-hover:rotate-12 transition-transform duration-300" />
              <span className="hidden sm:inline font-medium">Copy</span>
            </button>

            <button
              onClick={() => setShowQRCode(true)}
              className={`${currentTheme.buttonSecondary} rounded-2xl px-4 py-3 flex items-center space-x-2 ${currentTheme.hover} transition-all duration-300 transform hover:scale-105 backdrop-blur-md bg-opacity-80 border border-purple-500/20 group`}
              title="Share QR Code"
            >
              <QrCode className="w-5 h-5 group-hover:rotate-12 transition-transform duration-300" />
              <span className="hidden sm:inline font-medium">QR</span>
            </button>
            
            {(isAdmin || isSeniorMod) && (
              <button
                onClick={() => setShowSettings(true)}
                className={`${currentTheme.buttonSecondary} rounded-2xl px-4 py-3 flex items-center space-x-2 ${currentTheme.hover} transition-all duration-300 transform hover:scale-105 backdrop-blur-md bg-opacity-80 border border-purple-500/20 group`}
                title="Settings"
              >
                <Settings className="w-5 h-5 group-hover:rotate-90 transition-transform duration-300" />
                <span className="hidden sm:inline font-medium">Settings</span>
              </button>
            )}

            {!callState && (
              <button
                onClick={startCall}
                className={`${currentTheme.button} rounded-2xl px-4 py-3 flex items-center space-x-2 transform hover:scale-105 transition-all duration-300 shadow-lg backdrop-blur-md bg-opacity-90 border border-green-500/30 group`}
                title="Start Call"
              >
                <Phone className="w-5 h-5 group-hover:animate-bounce" />
                <span className="hidden sm:inline font-medium">Start Call</span>
              </button>
            )}

            {callState && !isInCall && (
              <button
                onClick={joinCall}
                className={`${currentTheme.button} rounded-2xl px-4 py-3 flex items-center space-x-2 transform hover:scale-105 transition-all duration-300 shadow-lg backdrop-blur-md bg-opacity-90 border border-green-500/30 group animate-pulse`}
                title="Join Call"
              >
                <Phone className="w-5 h-5 group-hover:animate-bounce" />
                <span className="hidden sm:inline font-medium">Join Call</span>
              </button>
            )}
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex overflow-hidden relative z-10">
        {/* Messages or Call Room */}
        <div className="flex-1 flex flex-col relative">
          {isInCall && callState && !isCallMinimized ? (
            <CallRoom
              socket={socket}
              roomId={roomId}
              username={username}
              users={users}
              callState={callState}
              onMinimize={minimizeCall}
            />
          ) : (
            <>
              {isInCall && (
                <div className={`${currentTheme.card} p-4 m-4 mb-2 flex justify-between items-center backdrop-blur-md bg-opacity-80 border border-green-500/30 rounded-2xl shadow-lg`}>
                  <div className="flex items-center space-x-3">
                    <div className="w-3 h-3 bg-green-500 rounded-full animate-pulse"></div>
                    <Phone className="w-5 h-5 text-green-500" />
                    <span className="text-sm font-medium">In call with {callState.participants.length} people</span>
                  </div>
                  <button 
                    onClick={() => setIsCallMinimized(false)}
                    className="text-sm text-purple-400 hover:text-purple-300 font-medium px-3 py-1 rounded-lg hover:bg-purple-500/20 transition-all duration-300"
                  >
                    Show call
                  </button>
                </div>
              )}
              
              <div className="flex-1 overflow-y-auto p-6 space-y-4 scrollbar-thin scrollbar-thumb-purple-500/30 scrollbar-track-transparent">
                {messages.map((msg) => (
                  <Message
                    key={msg.id}
                    message={msg}
                    currentTheme={currentTheme}
                    isAdmin={isAdmin}
                    isSeniorMod={isSeniorMod}
                    isModerator={isModerator}
                    deleteMessage={deleteMessage}
                  />
                ))}
                <div ref={messagesEndRef} />
              </div>
              
              {/* Message Input */}
              <div className={`border-t ${currentTheme.border} p-6 backdrop-blur-xl bg-opacity-80 relative`}>
                <div className="absolute inset-0 bg-gradient-to-r from-purple-500/10 via-pink-500/10 to-blue-500/10 backdrop-blur-xl"></div>
                <div className="relative z-10">
                  {joinCooldownRemaining > 0 && !(isAdmin || isSeniorMod || isModerator) && (
                    <div className="text-center text-sm text-purple-400 mb-4 flex items-center justify-center space-x-2 animate-pulse">
                      <Ghost className="w-4 h-4" />
                      <span className="font-medium">You can speak in {joinCooldownRemaining}s...</span>
                      <Sparkles className="w-4 h-4" />
                    </div>
                  )}
                  
                  <form onSubmit={sendMessage} className="flex space-x-3">
                    <input
                      type="file"
                      ref={fileInputRef}
                      onChange={handleFileUpload}
                      accept="image/*"
                      className="hidden"
                    />
                    
                    <button
                      type="button"
                      onClick={() => fileInputRef.current?.click()}
                      disabled={!roomSettings.allowImages && !(isAdmin || isSeniorMod || isModerator)}
                      className={`${currentTheme.buttonSecondary} rounded-2xl p-4 flex items-center disabled:opacity-50 disabled:cursor-not-allowed ${currentTheme.hover} transition-all duration-300 transform hover:scale-105 backdrop-blur-md bg-opacity-80 border border-purple-500/20 group`}
                      title="Upload Image"
                    >
                      <Image className="w-5 h-5 group-hover:rotate-12 transition-transform duration-300" />
                    </button>
                    
                    <div className="flex-1 relative">
                      <input
                        type="text"
                        value={message}
                        onChange={(e) => {
                          setMessage(e.target.value);
                          setIsTyping(e.target.value.length > 0);
                        }}
                        className={`${currentTheme.input} border rounded-2xl w-full px-6 py-4 focus:outline-none focus:ring-2 focus:ring-purple-500/50 focus:border-purple-500/50 transition-all duration-300 backdrop-blur-md bg-opacity-80 border-purple-500/20 text-base placeholder-gray-400`}
                        placeholder={
                          cooldown > 0 && !(isAdmin || isSeniorMod || isModerator) 
                            ? `Wait ${cooldown}s...` 
                            : "Whisper your secrets into the void..."
                        }
                        maxLength={500}
                        disabled={
                          (cooldown > 0 || 
                          (joinCooldown > 0 && joinCooldownRemaining > 0)) && 
                          !(isAdmin || isSeniorMod || isModerator)
                        }
                      />
                      {isTyping && (
                        <div className="absolute right-4 top-1/2 transform -translate-y-1/2">
                          <div className="flex space-x-1">
                            <div className="w-2 h-2 bg-purple-400 rounded-full animate-bounce"></div>
                            <div className="w-2 h-2 bg-purple-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                            <div className="w-2 h-2 bg-purple-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                          </div>
                        </div>
                      )}
                    </div>
                    
                    <button
                      type="submit"
                      disabled={
                        !message.trim() || 
                        (cooldown > 0 && !(isAdmin || isSeniorMod || isModerator)) || 
                        (joinCooldown > 0 && joinCooldownRemaining > 0 && !(isAdmin || isSeniorMod || isModerator))
                      }
                      className={`${currentTheme.button} rounded-2xl p-4 flex items-center disabled:opacity-50 disabled:cursor-not-allowed transform hover:scale-105 transition-all duration-300 shadow-lg backdrop-blur-md bg-opacity-90 border border-purple-500/30 group`}
                      title="Send Message"
                    >
                      <Send className="w-5 h-5 group-hover:translate-x-1 transition-transform duration-300" />
                    </button>
                  </form>
                </div>
              </div>
            </>
          )}
        </div>
        
        <UserList
          users={users}
          isAdmin={isAdmin}
          isSeniorMod={isSeniorMod}
          isModerator={isModerator}
          roomId={roomId}
          socket={socket}
          currentTheme={currentTheme}
          resetState={resetState}
        />
      </div>

      <SettingsModal
        showSettings={showSettings}
        setShowSettings={setShowSettings}
        users={users}
        isAdmin={isAdmin}
        isSeniorMod={isSeniorMod}
        roomSettings={roomSettings}
        setRoomSettings={setRoomSettings}
        socket={socket}
        roomId={roomId}
        currentTheme={currentTheme}
        resetState={resetState}
      />

      <QRCodeModal
        showQRCode={showQRCode}
        setShowQRCode={setShowQRCode}
        roomId={roomId}
        currentTheme={currentTheme}
      />
    </div>
  );
};

export default Chat;
