import React, { useState, useEffect, useRef, useCallback } from 'react';
import { 
  Mic, MicOff, Users, Phone, PhoneOff, Volume2, VolumeX, Settings, 
  Wifi, WifiOff, User, UserX, Clock, Signal, AlertCircle, Check,
  ChevronUp, ChevronDown, MoreVertical, Copy, UserPlus, Shield
} from 'lucide-react';

const CallRoom = ({ socket, roomId, username, users, callState }) => {
  const [participants, setParticipants] = useState([]);
  const [leftParticipants, setLeftParticipants] = useState([]);
  const [isMuted, setIsMuted] = useState(false);
  const [isDeafened, setIsDeafened] = useState(false);
  const [speakingUsers, setSpeakingUsers] = useState(new Set());
  const [mutedUsers, setMutedUsers] = useState(new Set());
  const [connectionQuality, setConnectionQuality] = useState({});
  const [callStartTime, setCallStartTime] = useState(null);
  const [callDuration, setCallDuration] = useState(0);
  const [showParticipantsList, setShowParticipantsList] = useState(false);
  const [showCallInfo, setShowCallInfo] = useState(false);
  const [audioLevels, setAudioLevels] = useState({});
  const [networkStats, setNetworkStats] = useState({});
  const [showSettings, setShowSettings] = useState(false);
  const [micSensitivity, setMicSensitivity] = useState(15);
  const [outputVolume, setOutputVolume] = useState(100);
  
  const userAudio = useRef(null);
  const peersRef = useRef([]);
  const streamRef = useRef(null);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const speakingTimeoutRef = useRef({});
  const audioLevelTimeoutRef = useRef({});
  const currentTheme = {
    card: 'bg-gray-900',
    textPrimary: 'text-white',
    textSecondary: 'text-gray-300',
    border: 'border-gray-700',
    accent: 'bg-indigo-600'
  };

  // Generate profile picture from username
  const generateProfilePicture = (username) => {
    const colors = [
      '#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FECA57',
      '#FF9FF3', '#54A0FF', '#5F27CD', '#00D2D3', '#FF9F43',
      '#6C5CE7', '#FD79A8', '#FDCB6E', '#6C5CE7', '#A29BFE'
    ];
    const firstLetter = username?.charAt(0).toUpperCase() || '?';
    const colorIndex = username ? username.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0) % colors.length : 0;
    return { letter: firstLetter, color: colors[colorIndex] };
  };

  // Enhanced voice activity detection with audio levels
  const setupVoiceActivityDetection = useCallback((stream, peerID) => {
    try {
      const audioContext = new (window.AudioContext || window.webkitAudioContext)();
      const analyser = audioContext.createAnalyser();
      const microphone = audioContext.createMediaStreamSource(stream);
      
      analyser.fftSize = 1024;
      analyser.minDecibels = -90;
      analyser.maxDecibels = -10;
      analyser.smoothingTimeConstant = 0.85;
      
      microphone.connect(analyser);
      
      const bufferLength = analyser.frequencyBinCount;
      const dataArray = new Uint8Array(bufferLength);
      
      const checkAudioLevel = () => {
        analyser.getByteFrequencyData(dataArray);
        const average = dataArray.reduce((a, b) => a + b) / bufferLength;
        const normalizedLevel = Math.min(100, Math.max(0, (average / 128) * 100));
        
        // Update audio levels for visualization
        setAudioLevels(prev => ({ ...prev, [peerID]: normalizedLevel }));
        
        // Clear audio level after inactivity
        if (audioLevelTimeoutRef.current[peerID]) {
          clearTimeout(audioLevelTimeoutRef.current[peerID]);
        }
        audioLevelTimeoutRef.current[peerID] = setTimeout(() => {
          setAudioLevels(prev => ({ ...prev, [peerID]: 0 }));
        }, 100);
        
        // Speaking detection
        if (average > micSensitivity && !mutedUsers.has(peerID)) {
          setSpeakingUsers(prev => new Set([...prev, peerID]));
          
          if (speakingTimeoutRef.current[peerID]) {
            clearTimeout(speakingTimeoutRef.current[peerID]);
          }
          
          speakingTimeoutRef.current[peerID] = setTimeout(() => {
            setSpeakingUsers(prev => {
              const newSet = new Set(prev);
              newSet.delete(peerID);
              return newSet;
            });
          }, 500);
        }
        
        if (audioContext.state !== 'closed') {
          requestAnimationFrame(checkAudioLevel);
        }
      };
      
      checkAudioLevel();
      
      return () => {
        microphone.disconnect();
        audioContext.close();
      };
    } catch (err) {
      console.error('Voice activity detection setup failed:', err);
    }
  }, [micSensitivity, mutedUsers]);

  // Monitor network quality
  const monitorNetworkQuality = useCallback((peer, peerID) => {
    const interval = setInterval(async () => {
      try {
        const stats = await peer.getStats();
        let bytesReceived = 0;
        let bytesSent = 0;
        let packetsLost = 0;
        let packetsReceived = 0;
        
        stats.forEach(report => {
          if (report.type === 'inbound-rtp' && report.kind === 'audio') {
            bytesReceived += report.bytesReceived || 0;
            packetsLost += report.packetsLost || 0;
            packetsReceived += report.packetsReceived || 0;
          } else if (report.type === 'outbound-rtp' && report.kind === 'audio') {
            bytesSent += report.bytesSent || 0;
          }
        });
        
        const packetLossRate = packetsReceived > 0 ? (packetsLost / packetsReceived) * 100 : 0;
        
        let quality = 'excellent';
        if (packetLossRate > 5) quality = 'poor';
        else if (packetLossRate > 2) quality = 'fair';
        else if (packetLossRate > 0.5) quality = 'good';
        
        setNetworkStats(prev => ({
          ...prev,
          [peerID]: {
            bytesReceived,
            bytesSent,
            packetsLost,
            packetsReceived,
            packetLossRate: packetLossRate.toFixed(1),
            quality
          }
        }));
        
        setConnectionQuality(prev => ({ ...prev, [peerID]: quality }));
        
      } catch (err) {
        console.error('Network monitoring error:', err);
      }
    }, 2000);
    
    peer.networkMonitor = interval;
  }, []);

  // Handle mute/unmute from other participants
  const handleParticipantMuted = ({ userId, isMuted: muteState }) => {
    setMutedUsers(prev => {
      const newSet = new Set(prev);
      if (muteState) {
        newSet.add(userId);
      } else {
        newSet.delete(userId);
      }
      return newSet;
    });
  };

  // Cleanup a specific peer
  const cleanupPeer = useCallback((peerID) => {
    const peerObj = peersRef.current.find(p => p.peerID === peerID);
    if (peerObj) {
      if (peerObj.peer.networkMonitor) {
        clearInterval(peerObj.peer.networkMonitor);
      }
      peerObj.peer.destroy();
      peersRef.current = peersRef.current.filter(p => p.peerID !== peerID);
    }
    
    // Clear timeouts
    if (speakingTimeoutRef.current[peerID]) {
      clearTimeout(speakingTimeoutRef.current[peerID]);
      delete speakingTimeoutRef.current[peerID];
    }
    
    if (audioLevelTimeoutRef.current[peerID]) {
      clearTimeout(audioLevelTimeoutRef.current[peerID]);
      delete audioLevelTimeoutRef.current[peerID];
    }
    
    // Remove from speaking users
    setSpeakingUsers(prev => {
      const newSet = new Set(prev);
      newSet.delete(peerID);
      return newSet;
    });
    
    // Clean up audio levels
    setAudioLevels(prev => {
      const newLevels = { ...prev };
      delete newLevels[peerID];
      return newLevels;
    });
  }, []);

  // Full cleanup
  const cleanup = useCallback(() => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    
    peersRef.current.forEach(({ peer }) => {
      if (peer.networkMonitor) {
        clearInterval(peer.networkMonitor);
      }
      peer.destroy();
    });
    peersRef.current = [];
    
    if (userAudio.current) {
      userAudio.current.srcObject = null;
    }
    
    if (audioContextRef.current) {
      audioContextRef.current.close();
    }
    
    // Clear all timeouts
    Object.values(speakingTimeoutRef.current).forEach(timeout => clearTimeout(timeout));
    Object.values(audioLevelTimeoutRef.current).forEach(timeout => clearTimeout(timeout));
    speakingTimeoutRef.current = {};
    audioLevelTimeoutRef.current = {};
    
    setSpeakingUsers(new Set());
    setAudioLevels({});
  }, []);

  // Create peer connection
  const createPeer = useCallback((userToSignal, callerID, stream) => {
    const existingPeer = peersRef.current.find(p => p.peerID === userToSignal);
    if (existingPeer) return existingPeer.peer;

    const peer = new (window.SimplePeer || window.Peer)({
      initiator: true,
      trickle: true,
      stream,
      config: {
        iceServers: [
          { urls: 'stun:stun.l.google.com:19302' },
          { urls: 'stun:stun1.l.google.com:19302' },
          { urls: 'stun:stun2.l.google.com:19302' },
          { urls: 'stun:stun3.l.google.com:19302' },
          { urls: 'stun:stun4.l.google.com:19302' }
        ]
      }
    });

    peer.on('signal', signal => {
      socket.emit('callSignal', { roomId, signal, to: userToSignal });
    });

    peer.on('connect', () => {
      console.log('Peer connected:', userToSignal);
      updateParticipantStatus(userToSignal, 'connected');
      monitorNetworkQuality(peer, userToSignal);
    });

    peer.on('stream', stream => {
      const audio = document.createElement('audio');
      audio.srcObject = stream;
      audio.autoplay = true;
      audio.volume = isDeafened ? 0 : (outputVolume / 100);
      document.body.appendChild(audio);
      
      setupVoiceActivityDetection(stream, userToSignal);
      peer.audioElement = audio;
    });

    peer.on('error', err => {
      console.error('Peer error:', err);
      updateParticipantStatus(userToSignal, 'disconnected');
      setConnectionQuality(prev => ({ ...prev, [userToSignal]: 'poor' }));
      cleanupPeer(userToSignal);
    });

    peer.on('close', () => {
      updateParticipantStatus(userToSignal, 'disconnected');
      if (peer.audioElement) {
        document.body.removeChild(peer.audioElement);
      }
      cleanupPeer(userToSignal);
    });

    return peer;
  }, [socket, roomId, cleanupPeer, isDeafened, outputVolume, setupVoiceActivityDetection, monitorNetworkQuality]);

  // Add peer connection
  const addPeer = useCallback((incomingSignal, callerID, stream) => {
    const existingPeer = peersRef.current.find(p => p.peerID === callerID);
    if (existingPeer) return existingPeer.peer;

    const peer = new (window.SimplePeer || window.Peer)({
      initiator: false,
      trickle: true,
      stream,
      config: {
        iceServers: [
          { urls: 'stun:stun.l.google.com:19302' },
          { urls: 'stun:stun1.l.google.com:19302' },
          { urls: 'stun:stun2.l.google.com:19302' },
          { urls: 'stun:stun3.l.google.com:19302' },
          { urls: 'stun:stun4.l.google.com:19302' }
        ]
      }
    });

    peer.on('signal', signal => {
      socket.emit('callSignal', { roomId, signal, to: callerID });
    });

    peer.on('connect', () => {
      console.log('Peer connected:', callerID);
      updateParticipantStatus(callerID, 'connected');
      monitorNetworkQuality(peer, callerID);
    });

    peer.on('stream', stream => {
      const audio = document.createElement('audio');
      audio.srcObject = stream;
      audio.autoplay = true;
      audio.volume = isDeafened ? 0 : (outputVolume / 100);
      document.body.appendChild(audio);
      
      setupVoiceActivityDetection(stream, callerID);
      peer.audioElement = audio;
    });

    peer.on('error', err => {
      console.error('Peer error:', err);
      updateParticipantStatus(callerID, 'disconnected');
      setConnectionQuality(prev => ({ ...prev, [callerID]: 'poor' }));
      cleanupPeer(callerID);
    });

    peer.on('close', () => {
      updateParticipantStatus(callerID, 'disconnected');
      if (peer.audioElement) {
        document.body.removeChild(peer.audioElement);
      }
      cleanupPeer(callerID);
    });

    // Handle signaling with retries
    const signalWithRetry = (retryCount = 0) => {
      try {
        peer.signal(incomingSignal);
      } catch (err) {
        console.error('Signaling error, retrying...', err);
        if (peer.destroyed || retryCount >= 3) return;
        setTimeout(() => signalWithRetry(retryCount + 1), 1000);
      }
    };

    signalWithRetry();
    return peer;
  }, [socket, roomId, cleanupPeer, isDeafened, outputVolume, setupVoiceActivityDetection, monitorNetworkQuality]);

  // Update participant status
  const updateParticipantStatus = (peerID, status) => {
    setParticipants(prev => prev.map(p => 
      p.peerID === peerID ? { ...p, connectionStatus: status } : p
    ));
  };

  // Update all participants
  const updateAllParticipants = useCallback(() => {
    if (!callState || !callState.participants) return;
    
    const allParticipants = [{ 
      peerID: socket.id, 
      username,
      isSelf: true,
      connectionStatus: 'connected',
      joinTime: callStartTime || Date.now()
    }];

    callState.participants.forEach(participantId => {
      if (participantId !== socket.id) {
        const existingPeer = peersRef.current.find(p => p.peerID === participantId);
        const existingParticipant = allParticipants.find(p => p.peerID === participantId);
        
        if (!existingParticipant) {
          allParticipants.push({
            peerID: participantId,
            username: users.find(u => u.id === participantId)?.username || 'Unknown',
            isSelf: false,
            connectionStatus: existingPeer ? 'connected' : 'connecting',
            joinTime: Date.now()
          });
        }
      }
    });

    setParticipants(allParticipants);
  }, [socket.id, username, callState, users, callStartTime]);

  // Format time duration
  const formatDuration = (seconds) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;
    
    if (hours > 0) {
      return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
    }
    return `${minutes}:${secs.toString().padStart(2, '0')}`;
  };

  // Get connection quality indicators
  const getConnectionQualityColor = (quality) => {
    switch (quality) {
      case 'excellent': return '#22c55e';
      case 'good': return '#84cc16';
      case 'fair': return '#f59e0b';
      case 'poor': return '#ef4444';
      default: return '#6b7280';
    }
  };

  const getConnectionQualityIcon = (quality) => {
    switch (quality) {
      case 'excellent': return <Signal className="w-4 h-4" />;
      case 'good': return <Signal className="w-4 h-4" />;
      case 'fair': return <AlertCircle className="w-4 h-4" />;
      case 'poor': return <WifiOff className="w-4 h-4" />;
      default: return <Signal className="w-4 h-4" />;
    }
  };

  // Toggle functions
  const toggleMute = () => {
    if (streamRef.current) {
      const newMuteState = !isMuted;
      streamRef.current.getAudioTracks().forEach(track => {
        track.enabled = !newMuteState;
      });
      setIsMuted(newMuteState);
      
      // Notify other participants
      socket.emit('participantMuted', { roomId, isMuted: newMuteState });
      
      // Update local muted users
      setMutedUsers(prev => {
        const newSet = new Set(prev);
        if (newMuteState) {
          newSet.add(socket.id);
        } else {
          newSet.delete(socket.id);
        }
        return newSet;
      });
    }
  };

  const toggleDeafen = () => {
    const newDeafenState = !isDeafened;
    setIsDeafened(newDeafenState);
    
    if (newDeafenState) {
      setIsMuted(true);
      if (streamRef.current) {
        streamRef.current.getAudioTracks().forEach(track => {
          track.enabled = false;
        });
      }
      setMutedUsers(prev => new Set([...prev, socket.id]));
    }
  };

  const leaveCall = () => {
    socket.emit('leaveCall', { roomId });
    cleanup();
  };

  const copyRoomId = () => {
    navigator.clipboard.writeText(roomId);
  };

  // Initialize call
  useEffect(() => {
    const initCall = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ 
          audio: {
            echoCancellation: true,
            noiseSuppression: true,
            autoGainControl: true,
            sampleRate: 44100
          }
        });
        
        streamRef.current = stream;
        if (userAudio.current) {
          userAudio.current.srcObject = stream;
        }

        setupVoiceActivityDetection(stream, socket.id);
        updateAllParticipants();
        setCallStartTime(Date.now());

        if (callState && callState.participants) {
          callState.participants
            .filter(participantId => participantId !== socket.id)
            .forEach(participantId => {
              const peer = createPeer(participantId, socket.id, stream);
              peersRef.current.push({
                peerID: participantId,
                peer,
                username: users.find(u => u.id === participantId)?.username || 'Unknown',
              });
            });
        }

      } catch (err) {
        console.error('Error accessing media devices:', err);
      }
    };

    const handleCallSignal = ({ signal, from }) => {
      if (!streamRef.current) return;
      
      const peerObj = peersRef.current.find(p => p.peerID === from);
      if (peerObj) {
        try {
          peerObj.peer.signal(signal);
        } catch (err) {
          console.error('Error signaling existing peer:', err);
          updateParticipantStatus(from, 'disconnected');
          cleanupPeer(from);
        }
      } else {
        const peer = addPeer(signal, from, streamRef.current);
        peersRef.current.push({
          peerID: from,
          peer,
          username: users.find(u => u.id === from)?.username || 'Unknown',
        });
      }
      updateAllParticipants();
    };

    const handleParticipantJoined = ({ userId, username: joinedUsername }) => {
      if (userId !== socket.id && streamRef.current) {
        const peer = createPeer(userId, socket.id, streamRef.current);
        peersRef.current.push({
          peerID: userId,
          peer,
          username: joinedUsername,
        });
      }
      updateAllParticipants();
    };

    const handleParticipantLeft = ({ userId, username: leftUsername }) => {
      cleanupPeer(userId);
      updateParticipantStatus(userId, 'left');
      
      // Add to left participants list
      setLeftParticipants(prev => [
        ...prev.filter(p => p.peerID !== userId),
        {
          peerID: userId,
          username: leftUsername || users.find(u => u.id === userId)?.username || 'Unknown',
          leftTime: Date.now()
        }
      ]);
    };

    const handleCallEnded = () => {
      cleanup();
      setCallStartTime(null);
      setCallDuration(0);
      setLeftParticipants([]);
    };

    initCall();

    socket.on('callSignal', handleCallSignal);
    socket.on('callParticipantJoined', handleParticipantJoined);
    socket.on('callParticipantLeft', handleParticipantLeft);
    socket.on('callEnded', handleCallEnded);
    socket.on('participantMuted', handleParticipantMuted);

    const interval = setInterval(updateAllParticipants, 5000);

    return () => {
      cleanup();
      clearInterval(interval);
      socket.off('callSignal', handleCallSignal);
      socket.off('callParticipantJoined', handleParticipantJoined);
      socket.off('callParticipantLeft', handleParticipantLeft);
      socket.off('callEnded', handleCallEnded);
      socket.off('participantMuted', handleParticipantMuted);
    };
  }, [socket, roomId, callState, users, createPeer, addPeer, cleanup, cleanupPeer, updateAllParticipants, setupVoiceActivityDetection]);

  // Update call duration
  useEffect(() => {
    if (!callStartTime) return;
    
    const interval = setInterval(() => {
      setCallDuration(Math.floor((Date.now() - callStartTime) / 1000));
    }, 1000);
    
    return () => clearInterval(interval);
  }, [callStartTime]);

  // Update audio volumes
  useEffect(() => {
    peersRef.current.forEach(({ peer }) => {
      if (peer.audioElement) {
        peer.audioElement.volume = isDeafened ? 0 : (outputVolume / 100);
      }
    });
  }, [isDeafened, outputVolume]);

  return (
    <div className={`flex-1 ${currentTheme.card} overflow-hidden flex flex-col relative`}>
      {/* Header */}
      <div className="p-4 border-b border-gray-700 relative">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <div className="flex items-center space-x-2">
              <div className="relative">
                <Phone className="w-5 h-5 text-green-400" />
                <div className="absolute -top-1 -right-1 w-2 h-2 bg-green-400 rounded-full animate-pulse" />
              </div>
              <div>
                <h2 className={`text-lg font-bold ${currentTheme.textPrimary} gradient-text`}>
                  Voice Call
                </h2>
                <div className="text-xs text-gray-400">
                  Room: {roomId}
                </div>
              </div>
            </div>
            
            {callStartTime && (
              <div className="flex items-center space-x-2 text-sm text-gray-400">
                <Clock className="w-4 h-4" />
                <span>{formatDuration(callDuration)}</span>
              </div>
            )}
          </div>
          
          <div className="flex items-center space-x-3">
            <button
              onClick={() => setShowParticipantsList(!showParticipantsList)}
              className="flex items-center space-x-2 px-3 py-1 rounded-lg bg-gray-800 hover:bg-gray-700 transition-colors"
            >
              <Users className="w-4 h-4" />
              <span className="text-sm">{participants.length}</span>
              {showParticipantsList ? <ChevronUp className="w-4 h-4" /> : <ChevronDown className="w-4 h-4" />}
            </button>
            
            <button
              onClick={copyRoomId}
              className="p-2 rounded-lg bg-gray-800 hover:bg-gray-700 transition-colors"
              title="Copy Room ID"
            >
              <Copy className="w-4 h-4" />
            </button>
            
            <button
              onClick={() => setShowSettings(!showSettings)}
              className="p-2 rounded-lg bg-gray-800 hover:bg-gray-700 transition-colors"
            >
              <Settings className="w-4 h-4" />
            </button>
          </div>
        </div>
      </div>

      {/* Settings Panel */}
      {showSettings && (
        <div className="absolute top-full left-0 right-0 bg-gray-800 border-b border-gray-700 p-4 z-50">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Microphone Sensitivity
              </label>
              <input
                type="range"
                min="5"
                max="50"
                value={micSensitivity}
                onChange={(e) => setMicSensitivity(parseInt(e.target.value))}
                className="w-full"
              />
              <div className="text-xs text-gray-400">Current: {micSensitivity}</div>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Output Volume
              </label>
              <input
                type="range"
                min="0"
                max="100"
                value={outputVolume}
                onChange={(e) => setOutputVolume(parseInt(e.target.value))}
                className="w-full"
              />
              <div className="text-xs text-gray-400">Current: {outputVolume}%</div>
            </div>
          </div>
        </div>
      )}

      {/* Participants List */}
      {showParticipantsList && (
        <div className="border-b border-gray-700 bg-gray-800/50 p-4 max-h-40 overflow-y-auto">
          <div className="space-y-2">
            {participants.map(({ peerID, username: participantUsername, isSelf, connectionStatus }) => {
              const profilePic = generateProfilePicture(participantUsername);
              const isSpeaking = speakingUsers.has(peerID);
              const isMutedUser = mutedUsers.has(peerID);
              const audioLevel = audioLevels[peerID] || 0;
              const quality = connectionQuality[peerID] || 'good';
              
              return (
                <div key={peerID} className="flex items-center space-x-3 p-2 rounded-lg hover:bg-gray-700/50">
                  <div className="relative">
                    <div 
                      className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-bold text-white
                        ${isSpeaking ? 'ring-2 ring-green-400' : ''}
                      `}
                      style={{ backgroundColor: profilePic.color }}
                    >
                      {profilePic.letter}
                    </div>
                    {isMutedUser && (
                      <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-red-500 rounded-full flex items-center justify-center">
                        <MicOff className="w-2 h-2 text-white" />
                      </div>
                    )}
                  </div>
                  
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center space-x-2">
                      <span className={`text-sm font-medium ${currentTheme.textPrimary} truncate`}>
                        {participantUsername}
                        {isSelf && <span className="text-blue-400 ml-1">(You)</span>}
                      </span>
                      {isSpeaking && (
                        <div className="flex items-center space-x-1">
                          <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse" />
                          <span className="text-xs text-green-400">Speaking</span>
                        </div>
                      )}
                    </div>
                    
                    {/* Audio level indicator */}
                    <div className="flex items-center space-x-2 mt-1">
                      <div className="w-16 h-1 bg-gray-600 rounded-full overflow-hidden">
                        <div 
                          className="h-full bg-green-400 transition-all duration-150 ease-out"
                          style={{ width: `${audioLevel}%` }}
                        />
                      </div>
                      <span className="text-xs text-gray-400">{Math.round(audioLevel)}%</span>
                    </div>
                  </div>
                  
                  <div className="flex items-center space-x-2">
                    <div 
                      className="flex items-center space-x-1"
                      style={{ color: getConnectionQualityColor(quality) }}
                      title={`Connection: ${quality}`}
                    >
                      {getConnectionQualityIcon(quality)}
                      <span className="text-xs capitalize">{quality}</span>
                    </div>
                    
                    {networkStats[peerID] && (
                      <div className="text-xs text-gray-400" title={`Packet Loss: ${networkStats[peerID].packetLossRate}%`}>
                        {networkStats[peerID].packetLossRate}%
                      </div>
                    )}
                  </div>
                </div>
              );
            })}
            
            {/* Left participants */}
            {leftParticipants.length > 0 && (
              <div className="border-t border-gray-600 pt-2 mt-2">
                <div className="text-xs text-gray-400 mb-2 flex items-center space-x-1">
                  <UserX className="w-3 h-3" />
                  <span>Left the call</span>
                </div>
                {leftParticipants.slice(-3).map(({ peerID, username: leftUsername, leftTime }) => {
                  const profilePic = generateProfilePicture(leftUsername);
                  const timeAgo = Math.floor((Date.now() - leftTime) / 1000);
                  
                  return (
                    <div key={`left-${peerID}`} className="flex items-center space-x-3 p-2 rounded-lg opacity-60">
                      <div 
                        className="w-8 h-8 rounded-full flex items-center justify-center text-sm font-bold text-white opacity-50"
                        style={{ backgroundColor: profilePic.color }}
                      >
                        {profilePic.letter}
                      </div>
                      <div className="flex-1 min-w-0">
                        <span className="text-sm text-gray-400 truncate">{leftUsername}</span>
                        <div className="text-xs text-gray-500">
                          Left {timeAgo < 60 ? `${timeAgo}s ago` : `${Math.floor(timeAgo / 60)}m ago`}
                        </div>
                      </div>
                      <UserX className="w-4 h-4 text-gray-500" />
                    </div>
                  );
                })}
              </div>
            )}
          </div>
        </div>
      )}

      {/* Main Participants Grid */}
      <div className="flex-1 p-4 overflow-y-auto">
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
          {participants.map(({ peerID, username: participantUsername, isSelf, connectionStatus }) => {
            const profilePic = generateProfilePicture(participantUsername);
            const isSpeaking = speakingUsers.has(peerID);
            const isMutedUser = mutedUsers.has(peerID);
            const audioLevel = audioLevels[peerID] || 0;
            const quality = connectionQuality[peerID] || 'good';
            
            return (
              <div 
                key={`${peerID}-${participantUsername}`}
                className={`
                  relative p-6 rounded-xl transition-all duration-200 border-2 backdrop-blur-sm
                  ${isSpeaking 
                    ? 'border-green-400 bg-green-900/20 shadow-lg shadow-green-400/20 scale-105' 
                    : 'border-gray-600 bg-gray-800/50 hover:bg-gray-800/70'
                  }
                  ${isSelf ? 'ring-2 ring-blue-400' : ''}
                  hover:border-gray-500 transition-all duration-200
                `}
              >
                {/* Profile Picture */}
                <div className="flex flex-col items-center space-y-4">
                  <div className="relative">
                    <div 
                      className={`
                        w-20 h-20 rounded-full flex items-center justify-center text-2xl font-bold text-white
                        ${isSpeaking ? 'ring-4 ring-green-400 ring-opacity-75 animate-pulse' : ''}
                        transition-all duration-200 shadow-lg
                      `}
                      style={{ backgroundColor: profilePic.color }}
                    >
                      {profilePic.letter}
                    </div>
                    
                    {/* Connection Quality Indicator */}
                    <div 
                      className="absolute -top-2 -right-2 w-6 h-6 rounded-full flex items-center justify-center shadow-lg"
                      style={{ backgroundColor: getConnectionQualityColor(quality) }}
                      title={`Connection: ${quality}`}
                    >
                      {getConnectionQualityIcon(quality)}
                    </div>
                    
                    {/* Mute Indicator */}
                    {isMutedUser && (
                      <div className="absolute -bottom-2 -right-2 w-6 h-6 bg-red-500 rounded-full flex items-center justify-center shadow-lg">
                        <MicOff className="w-3 h-3 text-white" />
                      </div>
                    )}
                  </div>
                  
                  {/* Username and Status */}
                  <div className="text-center">
                    <div className={`${currentTheme.textPrimary} font-medium text-sm mb-1`}>
                      {participantUsername}
                      {isSelf && <span className="text-blue-400 ml-1">(You)</span>}
                    </div>
                    
                    {/* Speaking/Muted Status */}
                    <div className="flex items-center justify-center space-x-2 mb-2">
                      {isSpeaking && !isMutedUser && (
                        <div className="flex items-center space-x-1 text-green-400">
                          <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse" />
                          <span className="text-xs font-medium">Speaking</span>
                        </div>
                      )}
                      {isMutedUser && (
                        <div className="flex items-center space-x-1 text-red-400">
                          <MicOff className="w-3 h-3" />
                          <span className="text-xs font-medium">Muted</span>
                        </div>
                      )}
                      {!isSpeaking && !isMutedUser && (
                        <div className="flex items-center space-x-1 text-gray-400">
                          <Mic className="w-3 h-3" />
                          <span className="text-xs">Listening</span>
                        </div>
                      )}
                    </div>
                    
                    {/* Audio Level Visualization */}
                    <div className="w-full">
                      <div className="flex items-center justify-center space-x-1 mb-1">
                        {[...Array(5)].map((_, i) => (
                          <div
                            key={i}
                            className={`
                              w-1 h-3 rounded-full transition-all duration-150
                              ${audioLevel > (i + 1) * 20 
                                ? (isSpeaking ? 'bg-green-400' : 'bg-blue-400') 
                                : 'bg-gray-600'
                              }
                            `}
                            style={{
                              height: audioLevel > (i + 1) * 20 ? `${8 + i * 2}px` : '4px'
                            }}
                          />
                        ))}
                      </div>
                      <div className="text-xs text-gray-400">
                        {Math.round(audioLevel)}% • {quality}
                      </div>
                    </div>
                    
                    {/* Network Stats */}
                    {networkStats[peerID] && (
                      <div className="text-xs text-gray-500 mt-1">
                        Loss: {networkStats[peerID].packetLossRate}%
                      </div>
                    )}
                  </div>
                </div>

                {/* Speaking Animation */}
                {isSpeaking && (
                  <div className="absolute inset-0 rounded-xl">
                    <div className="absolute inset-0 bg-green-400/10 rounded-xl animate-pulse" />
                    <div className="absolute -top-1 -right-1 w-4 h-4 bg-green-400 rounded-full">
                      <div className="w-full h-full bg-green-400 rounded-full animate-ping" />
                    </div>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>

      {/* Enhanced Controls */}
      <div className="p-4 border-t border-gray-700 bg-gray-800/50 backdrop-blur-sm">
        <div className="flex items-center justify-center space-x-4">
          <button
            onClick={toggleMute}
            className={`
              p-4 rounded-full transition-all duration-200 flex items-center space-x-2 shadow-lg
              ${isMuted 
                ? 'bg-red-500 hover:bg-red-600 text-white shadow-red-500/25' 
                : 'bg-gray-700 hover:bg-gray-600 text-gray-200 shadow-gray-700/25'
              }
              hover:scale-105 active:scale-95
            `}
            title={isMuted ? 'Unmute' : 'Mute'}
          >
            {isMuted ? <MicOff className="w-6 h-6" /> : <Mic className="w-6 h-6" />}
          </button>
          
          <button
            onClick={toggleDeafen}
            className={`
              p-4 rounded-full transition-all duration-200 flex items-center space-x-2 shadow-lg
              ${isDeafened 
                ? 'bg-red-500 hover:bg-red-600 text-white shadow-red-500/25' 
                : 'bg-gray-700 hover:bg-gray-600 text-gray-200 shadow-gray-700/25'
              }
              hover:scale-105 active:scale-95
            `}
            title={isDeafened ? 'Undeafen' : 'Deafen'}
          >
            {isDeafened ? <VolumeX className="w-6 h-6" /> : <Volume2 className="w-6 h-6" />}
          </button>
          
          <button
            onClick={leaveCall}
            className="p-4 bg-red-500 hover:bg-red-600 text-white rounded-full transition-all duration-200 shadow-lg shadow-red-500/25 hover:scale-105 active:scale-95"
            title="Leave Call"
          >
            <PhoneOff className="w-6 h-6" />
          </button>
        </div>
        
        {/* Control Labels */}
        <div className="flex items-center justify-center space-x-16 mt-3">
          <div className="text-center">
            <div className="text-xs text-gray-400 font-medium">
              {isMuted ? 'Unmute' : 'Mute'}
            </div>
            <div className="text-xs text-gray-500">
              {isMuted ? 'Ctrl+M' : 'Ctrl+M'}
            </div>
          </div>
          <div className="text-center">
            <div className="text-xs text-gray-400 font-medium">
              {isDeafened ? 'Undeafen' : 'Deafen'}
            </div>
            <div className="text-xs text-gray-500">
              {isDeafened ? 'Ctrl+D' : 'Ctrl+D'}
            </div>
          </div>
          <div className="text-center">
            <div className="text-xs text-gray-400 font-medium">
              Leave
            </div>
            <div className="text-xs text-gray-500">
              Ctrl+L
            </div>
          </div>
        </div>
        
        {/* Call Stats */}
        <div className="flex items-center justify-center space-x-6 mt-3 text-xs text-gray-400">
          <div className="flex items-center space-x-1">
            <Users className="w-3 h-3" />
            <span>{participants.length} participants</span>
          </div>
          <div className="flex items-center space-x-1">
            <Clock className="w-3 h-3" />
            <span>{formatDuration(callDuration)}</span>
          </div>
          <div className="flex items-center space-x-1">
            <Signal className="w-3 h-3" />
            <span>Quality: {Object.values(connectionQuality).filter(q => q === 'excellent' || q === 'good').length}/{participants.length - 1}</span>
          </div>
        </div>
      </div>

      {/* Hidden audio element for self */}
      <audio ref={userAudio} autoPlay muted />
    </div>
  );
};

export default CallRoom;
