import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Mic, MicOff, Users, Minimize2 } from 'lucide-react';
import Peer from 'simple-peer';

// ... existing getInitials and getColorFromName functions ...

const CallRoom = ({ socket, roomId, username, users, callState, onMinimize }) => {
  const [participants, setParticipants] = useState([]);
  const [isMuted, setIsMuted] = useState(false);
  const [localIsSpeaking, setLocalIsSpeaking] = useState(false);
  const userAudio = useRef(null);
  const peersRef = useRef([]);
  const streamRef = useRef(null);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const speakingCheckInterval = useRef(null);
  const currentTheme = THEMES.default;

  // Initialize audio without self-audio playback
  const initAudio = useCallback((stream) => {
    if (userAudio.current) {
      // Create a new stream without our own audio track to prevent echo
      const audioTracks = stream.getAudioTracks();
      const filteredStream = new MediaStream();
      audioTracks.forEach(track => filteredStream.addTrack(track));
      
      // Create an audio context for analysis but don't play our own audio
      const audioContext = new (window.AudioContext || window.webkitAudioContext)();
      const analyser = audioContext.createAnalyser();
      analyser.fftSize = 256;
      
      const source = audioContext.createMediaStreamSource(stream);
      source.connect(analyser);
      
      audioContextRef.current = audioContext;
      analyserRef.current = analyser;
      
      // Start checking speaking status
      const dataArray = new Uint8Array(analyser.frequencyBinCount);
      
      speakingCheckInterval.current = setInterval(() => {
        analyser.getByteFrequencyData(dataArray);
        const sum = dataArray.reduce((a, b) => a + b, 0);
        const avg = sum / dataArray.length;
        const isCurrentlySpeaking = avg > 10; // Threshold
        
        if (isCurrentlySpeaking !== localIsSpeaking) {
          setLocalIsSpeaking(isCurrentlySpeaking);
          socket.emit('speakingStatus', { roomId, isSpeaking: isCurrentlySpeaking });
        }
      }, 200);
    }
  }, [socket, roomId, localIsSpeaking]);

  // ... existing cleanup functions ...

  const createPeer = useCallback((userToSignal, callerID, stream) => {
    const existingPeer = peersRef.current.find(p => p.peerID === userToSignal);
    if (existingPeer) return existingPeer.peer;

    const peer = new Peer({
      initiator: true,
      trickle: true,
      stream,
      config: {
        iceServers: [
          { urls: 'stun:stun.l.google.com:19302' },
          { urls: 'stun:stun1.l.google.com:19302' }
        ]
      }
    });

    // ... existing peer event handlers ...

    return peer;
  }, [socket, roomId, cleanupPeer]);

  // ... existing addPeer function ...

  const toggleMute = () => {
    if (streamRef.current) {
      const newMutedState = !isMuted;
      streamRef.current.getAudioTracks().forEach(track => {
        track.enabled = !newMutedState;
      });
      setIsMuted(newMutedState);
      setLocalIsSpeaking(false);
      socket.emit('speakingStatus', { roomId, isSpeaking: false });
      socket.emit('muteStatus', { roomId, isMuted: newMutedState });
    }
  };

  // ... existing useEffect and other functions ...

  return (
    <div className={`flex-1 ${currentTheme.card} p-4 overflow-y-auto scrollbar-thin scrollbar-thumb-ghost-600 scrollbar-track-ghost-800`}>
      <div className="flex items-center justify-between mb-4">
        <h2 className={`text-lg font-bold ${currentTheme.textPrimary} gradient-text`}>
          Voice Call ({participants.filter(p => p.connectionStatus === 'connected').length + 1} participants)
        </h2>
        <div className="flex space-x-2">
          <button
            onClick={() => onMinimize()}
            className={`${currentTheme.buttonSecondary} rounded-xl p-2 flex items-center space-x-2 ${currentTheme.hover} transition-all duration-200`}
            title="Minimize call"
          >
            <Minimize2 className="w-5 h-5" />
          </button>
          <button
            onClick={toggleMute}
            className={`rounded-xl p-2 flex items-center space-x-2 ${
              isMuted ? 'bg-red-500 hover:bg-red-600' : 'bg-green-500 hover:bg-green-600'
            } text-white transition-all duration-200`}
            title={isMuted ? 'Unmute' : 'Mute'}
          >
            {isMuted ? <MicOff className="w-5 h-5" /> : <Mic className="w-5 h-5" />}
          </button>
        </div>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
        {/* Current user */}
        <div 
          className={`${currentTheme.buttonSecondary} rounded-xl p-4 flex flex-col items-center transition-all duration-300 ${
            localIsSpeaking ? 'ring-2 ring-purple-500 transform scale-105' : ''
          }`}
        >
          <div 
            className="w-16 h-16 rounded-full flex items-center justify-center text-2xl font-bold text-white mb-2 relative"
            style={{ backgroundColor: getColorFromName(username) }}
          >
            {getInitials(username)}
            {isMuted && (
              <div className="absolute bottom-0 right-0 w-6 h-6 bg-red-500 rounded-full flex items-center justify-center">
                <MicOff className="w-3 h-3 text-white" />
              </div>
            )}
          </div>
          <span className={`text-sm ${currentTheme.textPrimary} font-medium text-center`}>
            {username} (You)
          </span>
          <span className="text-xs text-green-500 mt-1">
            Connected
          </span>
          {localIsSpeaking && (
            <div className="flex space-x-1 mt-2">
              {[1, 2, 3].map((i) => (
                <div 
                  key={i}
                  className="w-1 h-1 bg-purple-500 rounded-full animate-bounce"
                  style={{ animationDelay: `${i * 0.1}s` }}
                ></div>
              ))}
            </div>
          )}
        </div>

        {/* Other participants */}
        {participants.map(({ peerID, username: participantUsername, isSpeaking, connectionStatus, initials, color, isMuted: remoteMuted }) => (
          <div 
            key={`${peerID}-${participantUsername}`}
            className={`${currentTheme.buttonSecondary} rounded-xl p-4 flex flex-col items-center transition-all duration-300 ${
              isSpeaking ? 'ring-2 ring-purple-500 transform scale-105' : ''
            }`}
          >
            <div 
              className="w-16 h-16 rounded-full flex items-center justify-center text-2xl font-bold text-white mb-2 relative"
              style={{ backgroundColor: color }}
            >
              {initials}
              {remoteMuted && (
                <div className="absolute bottom-0 right-0 w-6 h-6 bg-red-500 rounded-full flex items-center justify-center">
                  <MicOff className="w-3 h-3 text-white" />
                </div>
              )}
              {connectionStatus === 'connected' && !remoteMuted && (
                <div className="absolute bottom-0 right-0 w-4 h-4 bg-green-500 rounded-full border-2 border-white"></div>
              )}
            </div>
            
            <span className={`text-sm ${currentTheme.textPrimary} font-medium text-center`}>
              {participantUsername}
            </span>
            
            <span className={`text-xs mt-1 ${
              connectionStatus === 'connected' ? 'text-green-500' :
              connectionStatus === 'connecting' ? 'text-yellow-500' :
              'text-red-500'
            }`}>
              {connectionStatus === 'connected' ? remoteMuted ? 'Muted' : 'Connected' :
               connectionStatus === 'connecting' ? 'Connecting...' :
               'Disconnected'}
            </span>
            
            {isSpeaking && !remoteMuted && (
              <div className="flex space-x-1 mt-2">
                {[1, 2, 3].map((i) => (
                  <div 
                    key={i}
                    className="w-1 h-1 bg-purple-500 rounded-full animate-bounce"
                    style={{ animationDelay: `${i * 0.1}s` }}
                  ></div>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default CallRoom;
