import React, { useState, useEffect, useRef } from 'react';
import { Mic, MicOff, Users } from 'lucide-react';
import Peer from 'simple-peer';
import { THEMES } from '../constants';

const CallRoom = ({ socket, roomId, username, users, callState }) => {
  const [peers, setPeers] = useState([]);
  const [isMuted, setIsMuted] = useState(false);
  const userAudio = useRef(null);
  const peersRef = useRef([]);
  const streamRef = useRef(null);
  const currentTheme = THEMES.default;

  useEffect(() => {
    const initCall = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        streamRef.current = stream;
        if (userAudio.current) {
          userAudio.current.srcObject = stream;
        }

        const isHost = callState.hostId === socket.id;
        
        // Create peer connections for existing participants
        callState.participants.forEach(participantId => {
          if (participantId !== socket.id) {
            const peer = createPeer(participantId, socket.id, stream);
            peersRef.current.push({
              peerID: participantId,
              peer,
              username: users.find(u => u.id === participantId)?.username || 'Unknown',
            });
          }
        });

        socket.on('callSignal', ({ signal, from }) => {
          const peerObj = peersRef.current.find(p => p.peerID === from);
          if (peerObj) {
            peerObj.peer.signal(signal);
          } else {
            const peer = addPeer(signal, from, stream);
            peersRef.current.push({
              peerID: from,
              peer,
              username: users.find(u => u.id === from)?.username || 'Unknown',
            });
          }
        });

        socket.on('callParticipantJoined', ({ userId, username: joinedUsername }) => {
          if (userId !== socket.id) {
            const peer = createPeer(userId, socket.id, stream);
            peersRef.current.push({
              peerID: userId,
              peer,
              username: joinedUsername,
            });
          }
        });

        socket.on('callParticipantLeft', ({ userId }) => {
          const peerObj = peersRef.current.find(p => p.peerID === userId);
          if (peerObj) {
            peerObj.peer.destroy();
            peersRef.current = peersRef.current.filter(p => p.peerID !== userId);
          }
        });

        socket.on('callEnded', () => {
          cleanup();
        });

        // Update peers state
        setPeers(peersRef.current.map(p => ({
          peerID: p.peerID,
          peer: p.peer,
          username: p.username
        })));

      } catch (err) {
        console.error('Error accessing media devices:', err);
      }
    };

    initCall();

    return () => {
      cleanup();
      socket.off('callSignal');
      socket.off('callParticipantJoined');
      socket.off('callParticipantLeft');
      socket.off('callEnded');
    };
  }, [socket, roomId, callState, users]);

  const createPeer = (userToSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
      config: {
        iceServers: [
          { urls: 'stun:stun.l.google.com:19302' },
          { urls: 'stun:stun1.l.google.com:19302' }
        ]
      }
    });

    peer.on('signal', signal => {
      socket.emit('callSignal', { roomId, signal, to: userToSignal });
    });

    peer.on('error', err => {
      console.error('Peer error:', err);
      cleanupPeer(userToSignal);
    });

    peer.on('close', () => {
      cleanupPeer(userToSignal);
    });

    return peer;
  };

  const addPeer = (incomingSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
      config: {
        iceServers: [
          { urls: 'stun:stun.l.google.com:19302' },
          { urls: 'stun:stun1.l.google.com:19302' }
        ]
      }
    });

    peer.on('signal', signal => {
      socket.emit('callSignal', { roomId, signal, to: callerID });
    });

    peer.on('error', err => {
      console.error('Peer error:', err);
      cleanupPeer(callerID);
    });

    peer.on('close', () => {
      cleanupPeer(callerID);
    });

    peer.signal(incomingSignal);
    return peer;
  };

  const cleanupPeer = (peerID) => {
    const peerObj = peersRef.current.find(p => p.peerID === peerID);
    if (peerObj) {
      peerObj.peer.destroy();
      peersRef.current = peersRef.current.filter(p => p.peerID !== peerID);
      setPeers(prev => prev.filter(p => p.peerID !== peerID));
    }
  };

  const cleanup = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    
    peersRef.current.forEach(({ peer }) => {
      peer.destroy();
    });
    
    peersRef.current = [];
    setPeers([]);
    
    if (userAudio.current) {
      userAudio.current.srcObject = null;
    }
  };

  const toggleMute = () => {
    if (streamRef.current) {
      streamRef.current.getAudioTracks().forEach(track => {
        track.enabled = !track.enabled;
      });
      setIsMuted(!isMuted);
    }
  };

  return (
    <div className={`flex-1 ${currentTheme.card} p-4 overflow-y-auto scrollbar-thin scrollbar-thumb-ghost-600 scrollbar-track-ghost-800`}>
      <div className="flex items-center justify-between mb-4">
        <h2 className={`text-lg font-bold ${currentTheme.textPrimary} gradient-text`}>Voice Call</h2>
        <button
          onClick={toggleMute}
          className={`${currentTheme.buttonSecondary} rounded-xl p-2 flex items-center space-x-2 ${currentTheme.hover} transition-all duration-200`}
        >
          {isMuted ? <MicOff className="w-5 h-5" /> : <Mic className="w-5 h-5" />}
          <span>{isMuted ? 'Unmute' : 'Mute'}</span>
        </button>
      </div>

      <div className="space-y-4">
        <div className={`${currentTheme.buttonSecondary} rounded-xl p-3 flex items-center space-x-2`}>
          <Users className="w-5 h-5 text-purple-400" />
          <span className={`${currentTheme.textPrimary} text-sm`}>{username} (You)</span>
          <audio ref={userAudio} autoPlay muted={isMuted} />
        </div>
        {peers.map(({ peerID, username: peerUsername }) => (
          <div key={peerID} className={`${currentTheme.buttonSecondary} rounded-xl p-3 flex items-center space-x-2`}>
            <Users className="w-5 h-5 text-purple-400" />
            <span className={`${currentTheme.textPrimary} text-sm`}>{peerUsername}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CallRoom;
