import React, { useState, useEffect, useRef } from 'react';
import { Ghost, Users, Copy, Settings, Phone, PhoneOff, Image, Send, Sparkles, Shield, Zap } from 'lucide-react';
import Message from '../components/Message';
import UserList from '../components/UserList';
import SettingsModal from '../components/SettingsModal';
import QRCodeModal from '../components/QRCodeModal';
import CallRoom from '../components/CallRoom';
import { THEMES } from '../constants';

const Chat = ({
  socket,
  username,
  roomId,
  roomName,
  messages,
  setMessages,
  users,
  setUsers,
  isAdmin,
  setIsAdmin,
  isSeniorMod,
  setIsSeniorMod,
  isModerator,
  setIsModerator,
  roomSettings,
  setRoomSettings,
  bannedUsers,
  setBannedUsers,
  userJoinTime,
  setCurrentView,
  callState,
  setCallState,
  encrypt,
  resetState
}) => {
  const [message, setMessage] = useState('');
  const [showSettings, setShowSettings] = useState(false);
  const [showQRCode, setShowQRCode] = useState(false);
  const [cooldown, setCooldown] = useState(0);
  const [joinCooldown, setJoinCooldown] = useState(0);
  const [isInCall, setIsInCall] = useState(false);
  const [isCallMinimized, setIsCallMinimized] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);
  const fileInputRef = useRef(null);
  const currentTheme = THEMES[roomSettings.theme] || THEMES.default;

  useEffect(() => {
    socket.on('cooldownSet', (cooldown) => {
      setCooldown(cooldown);
    });

    socket.on('joinCooldownSet', (cooldown) => {
      setJoinCooldown(cooldown);
    });

    socket.on('callStarted', (newCallState) => {
      setCallState(newCallState);
    });

    socket.on('callEnded', () => {
      setCallState(null);
      setIsInCall(false);
    });

    return () => {
      socket.off('cooldownSet');
      socket.off('joinCooldownSet');
      socket.off('callStarted');
      socket.off('callEnded');
    };
  }, [socket, setCallState]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    if (cooldown > 0) {
      const timer = setInterval(() => {
        setCooldown(prev => {
          if (prev <= 0) {
            clearInterval(timer);
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [cooldown]);

  useEffect(() => {
    if (joinCooldown > 0 && userJoinTime) {
      const timer = setInterval(() => {
        const timePassed = Math.floor((Date.now() - userJoinTime) / 1000);
        const remaining = Math.max(0, joinCooldown - timePassed);
        if (remaining <= 0) {
          clearInterval(timer);
        }
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [joinCooldown, userJoinTime]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file && file.type.startsWith('image/')) {
      sendImage(file);
    }
  };

  const sendMessage = (e) => {
    e.preventDefault();
    if (!message.trim()) return;
    
    setIsTyping(true);
    setTimeout(() => setIsTyping(false), 200);
    
    const isPrivileged = isAdmin || isSeniorMod || isModerator;
    
    if (!isPrivileged) {
      const timePassed = userJoinTime ? Math.floor((Date.now() - userJoinTime) / 1000) : 0;
      if (joinCooldown > 0 && timePassed < joinCooldown) {
        const remaining = joinCooldown - timePassed;
        setMessages(prev => [...prev, {
          id: Date.now(),
          type: 'system',
          content: `✨ Patience, spirit... You must wait ${remaining}s before speaking...`,
          timestamp: new Date().toISOString()
        }]);
        return;
      }

      if (cooldown > 0) {
        setMessages(prev => [...prev, {
          id: Date.now(),
          type: 'system',
          content: `⏱️ Your ethereal energy needs ${cooldown}s to recharge...`,
          timestamp: new Date().toISOString()
        }]);
        return;
      }

      const containsLink = /https?:\/\/[^\s]+/.test(message);
      if (!roomSettings.allowLinks && containsLink) {
        setMessages(prev => [...prev, {
          id: Date.now(),
          type: 'system',
          content: '🔗 Portal links are forbidden in this mystical realm...',
          timestamp: new Date().toISOString()
        }]);
        return;
      }
    }

    const messageData = {
      id: Date.now(),
      username,
      content: message.trim(),
      timestamp: new Date().toISOString(),
      type: 'message'
    };

    const encryptedMessage = encrypt(messageData);
    socket.emit('message', { roomId, message: encryptedMessage });
    setMessage('');
    if (!isPrivileged) {
      setCooldown(roomSettings.cooldown);
    }
  };

  const sendImage = (file) => {
    if (!roomSettings.allowImages && !(isAdmin || isSeniorMod || isModerator)) {
      setMessages(prev => [...prev, {
        id: Date.now(),
        type: 'system',
        content: '🖼️ Visual manifestations are disabled in this sacred space...',
        timestamp: new Date().toISOString()
      }]);
      return;
    }

    const reader = new FileReader();
    reader.onload = (e) => {
      const messageData = {
        id: Date.now(),
        username,
        content: e.target.result,
        timestamp: new Date().toISOString(),
        type: 'image'
      };

      const encryptedMessage = encrypt(messageData);
      socket.emit('message', { roomId, message: encryptedMessage });
      if (!(isAdmin || isSeniorMod || isModerator)) {
        setCooldown(roomSettings.cooldown);
      }
    };
    reader.readAsDataURL(file);
  };

  const deleteMessage = (messageId) => {
    socket.emit('deleteMessage', { roomId, messageId });
  };

  const copyRoomLink = () => {
    const link = `${window.location.origin}?room=${roomId}`;
    navigator.clipboard.writeText(link);
    setMessages(prev => [...prev, {
      id: Date.now(),
      type: 'system',
      content: '✨ Portal link manifested in your clipboard!',
      timestamp: new Date().toISOString()
    }]);
  };

  const startCall = () => {
    socket.emit('startCall', { roomId });
    setIsInCall(true);
    setIsCallMinimized(false);
  };

  const joinCall = () => {
    socket.emit('joinCall', { roomId });
    setIsInCall(true);
    setIsCallMinimized(false);
  };

  const minimizeCall = () => {
    setIsCallMinimized(true);
  };

  const timePassed = userJoinTime ? Math.floor((Date.now() - userJoinTime) / 1000) : 0;
  const joinCooldownRemaining = Math.max(0, joinCooldown - timePassed);

  return (
    <div className={`h-screen ${currentTheme.bg} flex flex-col relative overflow-hidden`}>
      {/* Animated Background */}
      <div className="absolute inset-0 opacity-10">
        <div className="absolute top-10 left-10 w-72 h-72 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl animate-pulse-slow"></div>
        <div className="absolute top-10 right-10 w-72 h-72 bg-cyan-500 rounded-full mix-blend-multiply filter blur-xl animate-pulse-slow animation-delay-2000"></div>
        <div className="absolute bottom-10 left-1/2 w-72 h-72 bg-pink-500 rounded-full mix-blend-multiply filter blur-xl animate-pulse-slow animation-delay-4000"></div>
      </div>

      {/* Header */}
      <div className={`${currentTheme.card} border-b ${currentTheme.border} backdrop-blur-xl bg-opacity-80 relative z-10`}>
        <div className="p-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <div className="relative">
                <div className="absolute -inset-1 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full blur opacity-75 animate-pulse"></div>
                <div className="relative">
                  <Ghost className="w-10 h-10 text-purple-400 animate-pulse-slow" />
                </div>
              </div>
              <div>
                <h1 className="text-2xl font-bold gradient-text tracking-tight flex items-center space-x-2">
                  <span>{roomName || 'Unnamed Realm'}</span>
                  <Sparkles className="w-5 h-5 text-yellow-400 animate-pulse" />
                </h1>
                <p className={`text-sm ${currentTheme.textSecondary} flex items-center space-x-2`}>
                  <Shield className="w-4 h-4" />
                  <span>ID: {roomId}</span>
                  <span className="text-green-400 animate-pulse">• Live</span>
                </p>
              </div>
            </div>
            
            <div className="flex items-center space-x-3">
              <div className={`${currentTheme.buttonSecondary} rounded-2xl px-4 py-3 flex items-center space-x-3 shadow-lg backdrop-blur-md`}>
                <div className="relative">
                  <Users className="w-5 h-5 text-purple-400" />
                  <div className="absolute -top-1 -right-1 w-3 h-3 bg-green-500 rounded-full animate-pulse"></div>
                </div>
                <span className={`font-semibold ${currentTheme.textPrimary}`}>{users.length}</span>
              </div>
              
              <button
                onClick={copyRoomLink}
                className={`${currentTheme.buttonSecondary} rounded-2xl px-4 py-3 flex items-center space-x-2 ${currentTheme.hover} transition-all duration-300 transform hover:scale-105 shadow-lg backdrop-blur-md`}
                title="Copy Realm Portal"
              >
                <Copy className="w-5 h-5" />
                <span className="hidden sm:inline font-medium">Share</span>
              </button>

              <button
                onClick={() => setShowQRCode(true)}
                className={`${currentTheme.buttonSecondary} rounded-2xl px-4 py-3 flex items-center space-x-2 ${currentTheme.hover} transition-all duration-300 transform hover:scale-105 shadow-lg backdrop-blur-md`}
                title="Mystical QR Portal"
              >
                <Zap className="w-5 h-5" />
                <span className="hidden sm:inline font-medium">QR</span>
              </button>
              
              {(isAdmin || isSeniorMod) && (
                <button
                  onClick={() => setShowSettings(true)}
                  className={`${currentTheme.buttonSecondary} rounded-2xl px-4 py-3 flex items-center space-x-2 ${currentTheme.hover} transition-all duration-300 transform hover:scale-105 shadow-lg backdrop-blur-md`}
                  title="Realm Configuration"
                >
                  <Settings className="w-5 h-5" />
                  <span className="hidden sm:inline font-medium">Settings</span>
                </button>
              )}

              {!callState && (
                <button
                  onClick={startCall}
                  className={`${currentTheme.button} rounded-2xl px-4 py-3 flex items-center space-x-2 transform hover:scale-105 transition-all duration-300 shadow-lg shadow-purple-500/25`}
                  title="Begin Ethereal Call"
                >
                  <Phone className="w-5 h-5" />
                  <span className="hidden sm:inline font-medium">Call</span>
                </button>
              )}

              {callState && !isInCall && (
                <button
                  onClick={joinCall}
                  className={`${currentTheme.button} rounded-2xl px-4 py-3 flex items-center space-x-2 transform hover:scale-105 transition-all duration-300 shadow-lg shadow-green-500/25 animate-pulse`}
                  title="Join Ethereal Call"
                >
                  <Phone className="w-5 h-5" />
                  <span className="hidden sm:inline font-medium">Join</span>
                </button>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex overflow-hidden relative z-10">
        {/* Messages or Call Room */}
        <div className="flex-1 flex flex-col">
          {isInCall && callState && !isCallMinimized ? (
            <CallRoom
              socket={socket}
              roomId={roomId}
              username={username}
              users={users}
              callState={callState}
              onMinimize={minimizeCall}
            />
          ) : (
            <>
              {isInCall && (
                <div className={`${currentTheme.card} m-4 p-4 rounded-2xl shadow-lg backdrop-blur-md bg-opacity-80 border border-green-500/20`}>
                  <div className="flex justify-between items-center">
                    <div className="flex items-center space-x-3">
                      <div className="relative">
                        <Phone className="w-5 h-5 text-green-400" />
                        <div className="absolute -top-1 -right-1 w-3 h-3 bg-green-500 rounded-full animate-pulse"></div>
                      </div>
                      <span className="font-medium">Ethereal communion with {callState.participants.length} spirits</span>
                    </div>
                    <button 
                      onClick={() => setIsCallMinimized(false)}
                      className="text-sm font-medium text-purple-400 hover:text-purple-300 px-3 py-1 rounded-full bg-purple-500/10 hover:bg-purple-500/20 transition-all duration-200"
                    >
                      Expand Call
                    </button>
                  </div>
                </div>
              )}
              
              <div className="flex-1 overflow-y-auto p-6 space-y-4 scrollbar-thin scrollbar-thumb-purple-600 scrollbar-track-transparent">
                {messages.map((msg) => (
                  <div key={msg.id} className="animate-slide-up">
                    <Message
                      message={msg}
                      currentTheme={currentTheme}
                      isAdmin={isAdmin}
                      isSeniorMod={isSeniorMod}
                      isModerator={isModerator}
                      deleteMessage={deleteMessage}
                    />
                  </div>
                ))}
                {isTyping && (
                  <div className="flex items-center space-x-2 text-purple-400 animate-pulse">
                    <Ghost className="w-4 h-4" />
                    <span className="text-sm">Channeling ethereal energy...</span>
                  </div>
                )}
                <div ref={messagesEndRef} />
              </div>
              
              {/* Message Input */}
              <div className="p-6 backdrop-blur-xl bg-opacity-80 border-t border-purple-500/20">
                {joinCooldownRemaining > 0 && !(isAdmin || isSeniorMod || isModerator) && (
                  <div className="text-center mb-4 p-3 rounded-2xl bg-purple-500/10 border border-purple-500/20">
                    <div className="flex items-center justify-center space-x-2 text-purple-400 animate-pulse">
                      <Ghost className="w-5 h-5" />
                      <span className="font-medium">Your spiritual energy is gathering... {joinCooldownRemaining}s remaining</span>
                    </div>
                  </div>
                )}
                <form onSubmit={sendMessage} className="flex space-x-4">
                  <input
                    type="file"
                    ref={fileInputRef}
                    onChange={handleFileUpload}
                    accept="image/*"
                    className="hidden"
                  />
                  
                  <button
                    type="button"
                    onClick={() => fileInputRef.current?.click()}
                    disabled={!roomSettings.allowImages && !(isAdmin || isSeniorMod || isModerator)}
                    className={`${currentTheme.buttonSecondary} rounded-2xl p-4 flex items-center disabled:opacity-50 disabled:cursor-not-allowed ${currentTheme.hover} transition-all duration-300 transform hover:scale-105 shadow-lg backdrop-blur-md`}
                  >
                    <Image className="w-5 h-5" />
                  </button>
                  
                  <div className="flex-1 relative">
                    <input
                      type="text"
                      value={message}
                      onChange={(e) => setMessage(e.target.value)}
                      className={`${currentTheme.input} border-2 border-purple-500/20 rounded-2xl w-full px-6 py-4 focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all duration-300 backdrop-blur-md bg-opacity-80 text-lg`}
                      placeholder={
                        cooldown > 0 && !(isAdmin || isSeniorMod || isModerator) 
                          ? `⏱️ Recharging... ${cooldown}s` 
                          : "✨ Channel your ethereal thoughts..."
                      }
                      maxLength={500}
                      disabled={
                        (cooldown > 0 || 
                        (joinCooldown > 0 && joinCooldownRemaining > 0)) && 
                        !(isAdmin || isSeniorMod || isModerator)
                      }
                    />
                    {message && (
                      <div className="absolute right-4 top-1/2 transform -translate-y-1/2 text-xs text-purple-400">
                        {message.length}/500
                      </div>
                    )}
                  </div>
                  
                  <button
                    type="submit"
                    disabled={
                      !message.trim() || 
                      (cooldown > 0 && !(isAdmin || isSeniorMod || isModerator)) || 
                      (joinCooldown > 0 && joinCooldownRemaining > 0 && !(isAdmin || isSeniorMod || isModerator))
                    }
                    className={`${currentTheme.button} rounded-2xl px-6 py-4 flex items-center space-x-2 disabled:opacity-50 disabled:cursor-not-allowed transform hover:scale-105 transition-all duration-300 shadow-lg shadow-purple-500/25 font-medium`}
                  >
                    <Send className="w-5 h-5" />
                    <span className="hidden sm:inline">Send</span>
                  </button>
                </form>
              </div>
            </>
          )}
        </div>
        
        <UserList
          users={users}
          isAdmin={isAdmin}
          isSeniorMod={isSeniorMod}
          isModerator={isModerator}
          roomId={roomId}
          socket={socket}
          currentTheme={currentTheme}
          resetState={resetState}
        />
      </div>

      <SettingsModal
        showSettings={showSettings}
        setShowSettings={setShowSettings}
        users={users}
        isAdmin={isAdmin}
        isSeniorMod={isSeniorMod}
        roomSettings={roomSettings}
        setRoomSettings={setRoomSettings}
        socket={socket}
        roomId={roomId}
        currentTheme={currentTheme}
        resetState={resetState}
      />

      <QRCodeModal
        showQRCode={showQRCode}
        setShowQRCode={setShowQRCode}
        roomId={roomId}
        currentTheme={currentTheme}
      />
    </div>
  );
};

export default Chat;
