import React, { useState, useEffect, useRef } from 'react';
import { Ghost, Users, Copy, Settings, Phone, PhoneOff, Image, Send, Share2, QrCode, Crown, Shield, Eye, Minimize2, Maximize2, Volume2, VolumeX } from 'lucide-react';

// Mock components for demo
const Message = ({ message, currentTheme, isAdmin, isSeniorMod, isModerator, deleteMessage }) => (
  <div className={`p-4 rounded-2xl ${currentTheme.messageBg} backdrop-blur-sm border ${currentTheme.border} hover:shadow-lg transition-all duration-300`}>
    <div className="flex items-center space-x-2 mb-2">
      <div className="w-8 h-8 bg-gradient-to-br from-purple-500 to-pink-500 rounded-full flex items-center justify-center">
        <span className="text-white text-sm font-bold">{message.username?.[0] || 'U'}</span>
      </div>
      <span className={`font-medium ${currentTheme.textPrimary}`}>{message.username || 'Anonymous'}</span>
      <span className={`text-xs ${currentTheme.textSecondary}`}>
        {new Date(message.timestamp).toLocaleTimeString()}
      </span>
    </div>
    <p className={`${currentTheme.textPrimary} leading-relaxed`}>{message.content}</p>
  </div>
);

const UserList = ({ users, isAdmin, isSeniorMod, isModerator, roomId, socket, currentTheme, resetState }) => (
  <div className={`w-80 ${currentTheme.card} border-l ${currentTheme.border} p-6 overflow-y-auto`}>
    <h3 className={`text-lg font-bold ${currentTheme.textPrimary} mb-4 flex items-center space-x-2`}>
      <Users className="w-5 h-5 text-purple-400" />
      <span>Spirits ({users.length})</span>
    </h3>
    <div className="space-y-3">
      {users.map((user, index) => (
        <div key={index} className={`p-3 rounded-xl ${currentTheme.buttonSecondary} hover:${currentTheme.hover} transition-all duration-300`}>
          <div className="flex items-center space-x-3">
            <div className="w-10 h-10 bg-gradient-to-br from-purple-500 to-pink-500 rounded-full flex items-center justify-center">
              <span className="text-white font-bold">{user.username?.[0] || 'U'}</span>
            </div>
            <div className="flex-1">
              <div className="flex items-center space-x-2">
                <span className={`font-medium ${currentTheme.textPrimary}`}>{user.username || 'Anonymous'}</span>
                {user.isAdmin && <Crown className="w-4 h-4 text-yellow-400" />}
                {user.isSeniorMod && <Shield className="w-4 h-4 text-blue-400" />}
                {user.isModerator && <Eye className="w-4 h-4 text-green-400" />}
              </div>
              <div className="flex items-center space-x-1 mt-1">
                <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
                <span className={`text-xs ${currentTheme.textSecondary}`}>Online</span>
              </div>
            </div>
          </div>
        </div>
      ))}
    </div>
  </div>
);

const SettingsModal = ({ showSettings, setShowSettings, users, isAdmin, isSeniorMod, roomSettings, setRoomSettings, socket, roomId, currentTheme, resetState }) => {
  if (!showSettings) return null;
  
  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4">
      <div className={`${currentTheme.card} rounded-3xl p-8 max-w-md w-full border ${currentTheme.border} shadow-2xl`}>
        <h2 className={`text-2xl font-bold ${currentTheme.textPrimary} mb-6`}>Realm Settings</h2>
        <div className="space-y-4">
          <div>
            <label className={`block text-sm font-medium ${currentTheme.textPrimary} mb-2`}>
              Message Cooldown (seconds)
            </label>
            <input
              type="number"
              value={roomSettings.cooldown}
              onChange={(e) => setRoomSettings({...roomSettings, cooldown: parseInt(e.target.value)})}
              className={`w-full ${currentTheme.input} rounded-xl p-3 border focus:ring-2 focus:ring-purple-500`}
            />
          </div>
          <div className="flex items-center justify-between">
            <span className={`text-sm font-medium ${currentTheme.textPrimary}`}>Allow Links</span>
            <button
              onClick={() => setRoomSettings({...roomSettings, allowLinks: !roomSettings.allowLinks})}
              className={`w-12 h-6 rounded-full transition-colors ${roomSettings.allowLinks ? 'bg-purple-500' : 'bg-gray-600'}`}
            >
              <div className={`w-5 h-5 rounded-full bg-white transition-transform ${roomSettings.allowLinks ? 'translate-x-6' : 'translate-x-0.5'}`}></div>
            </button>
          </div>
        </div>
        <div className="flex space-x-3 mt-8">
          <button
            onClick={() => setShowSettings(false)}
            className={`flex-1 ${currentTheme.buttonSecondary} rounded-xl py-3 font-medium transition-all duration-300`}
          >
            Cancel
          </button>
          <button
            onClick={() => {
              socket.emit('updateRoomSettings', { roomId, settings: roomSettings });
              setShowSettings(false);
            }}
            className={`flex-1 ${currentTheme.button} rounded-xl py-3 font-medium transition-all duration-300`}
          >
            Save
          </button>
        </div>
      </div>
    </div>
  );
};

const QRCodeModal = ({ showQRCode, setShowQRCode, roomId, currentTheme }) => {
  if (!showQRCode) return null;
  
  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4">
      <div className={`${currentTheme.card} rounded-3xl p-8 max-w-sm w-full border ${currentTheme.border} shadow-2xl text-center`}>
        <h2 className={`text-2xl font-bold ${currentTheme.textPrimary} mb-6`}>Share Realm</h2>
        <div className="w-48 h-48 bg-white rounded-2xl mx-auto mb-6 flex items-center justify-center">
          <QrCode className="w-32 h-32 text-gray-400" />
        </div>
        <p className={`text-sm ${currentTheme.textSecondary} mb-6`}>
          Scan this QR code to join the realm
        </p>
        <button
          onClick={() => setShowQRCode(false)}
          className={`w-full ${currentTheme.button} rounded-xl py-3 font-medium transition-all duration-300`}
        >
          Close
        </button>
      </div>
    </div>
  );
};

const CallRoom = ({ socket, roomId, username, users, callState, onMinimize }) => (
  <div className="flex-1 bg-gradient-to-br from-purple-900/20 to-pink-900/20 p-6 flex flex-col">
    <div className="flex items-center justify-between mb-6">
      <h2 className="text-2xl font-bold text-white">Voice Call</h2>
      <button
        onClick={onMinimize}
        className="p-2 bg-white/10 rounded-xl hover:bg-white/20 transition-colors"
      >
        <Minimize2 className="w-5 h-5 text-white" />
      </button>
    </div>
    <div className="flex-1 grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
      {callState.participants.map((participant, index) => (
        <div key={index} className="bg-black/30 rounded-2xl p-4 flex flex-col items-center justify-center">
          <div className="w-16 h-16 bg-gradient-to-br from-purple-500 to-pink-500 rounded-full flex items-center justify-center mb-3">
            <span className="text-white text-xl font-bold">{participant.username?.[0] || 'U'}</span>
          </div>
          <span className="text-white font-medium">{participant.username}</span>
          <div className="flex items-center space-x-2 mt-2">
            <Volume2 className="w-4 h-4 text-green-400" />
            <div className="flex space-x-1">
              {[...Array(5)].map((_, i) => (
                <div key={i} className="w-1 h-3 bg-green-400 rounded-full animate-pulse"></div>
              ))}
            </div>
          </div>
        </div>
      ))}
    </div>
    <div className="flex items-center justify-center space-x-4 mt-6">
      <button className="p-4 bg-green-500 rounded-full hover:bg-green-600 transition-colors">
        <Volume2 className="w-6 h-6 text-white" />
      </button>
      <button className="p-4 bg-red-500 rounded-full hover:bg-red-600 transition-colors">
        <PhoneOff className="w-6 h-6 text-white" />
      </button>
    </div>
  </div>
);

const THEMES = {
  default: {
    bg: 'bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900',
    card: 'bg-slate-800/50 backdrop-blur-md',
    messageBg: 'bg-slate-800/30',
    button: 'bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 text-white',
    buttonSecondary: 'bg-slate-700/50 hover:bg-slate-700 text-slate-200',
    input: 'bg-slate-800/50 border-slate-600 text-slate-100 placeholder-slate-400',
    textPrimary: 'text-slate-100',
    textSecondary: 'text-slate-400',
    border: 'border-slate-700',
    hover: 'hover:bg-slate-700/50'
  }
};

const Chat = ({
  socket,
  username,
  roomId,
  roomName,
  messages,
  setMessages,
  users,
  setUsers,
  isAdmin,
  setIsAdmin,
  isSeniorMod,
  setIsSeniorMod,
  isModerator,
  setIsModerator,
  roomSettings,
  setRoomSettings,
  bannedUsers,
  setBannedUsers,
  userJoinTime,
  setCurrentView,
  callState,
  setCallState,
  encrypt,
  resetState
}) => {
  const [message, setMessage] = useState('');
  const [showSettings, setShowSettings] = useState(false);
  const [showQRCode, setShowQRCode] = useState(false);
  const [cooldown, setCooldown] = useState(0);
  const [joinCooldown, setJoinCooldown] = useState(0);
  const [isInCall, setIsInCall] = useState(false);
  const [isCallMinimized, setIsCallMinimized] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);
  const fileInputRef = useRef(null);
  const currentTheme = THEMES[roomSettings?.theme] || THEMES.default;

  // Mock data for demo
  const mockMessages = [
    { id: 1, username: 'GhostWhisperer', content: 'Welcome to the ethereal realm!', timestamp: new Date().toISOString(), type: 'message' },
    { id: 2, username: 'ShadowWalker', content: 'The spirits are restless tonight...', timestamp: new Date().toISOString(), type: 'message' },
    { id: 3, type: 'system', content: 'A new soul has joined the realm...', timestamp: new Date().toISOString() }
  ];

  const mockUsers = [
    { username: 'GhostWhisperer', isAdmin: true },
    { username: 'ShadowWalker', isSeniorMod: true },
    { username: 'EtherealSpirit', isModerator: true },
    { username: 'PhantomWanderer' }
  ];

  const mockRoomSettings = {
    cooldown: 5,
    allowLinks: true,
    allowImages: true,
    theme: 'default'
  };

  useEffect(() => {
    if (socket) {
      socket.on('cooldownSet', (cooldown) => {
        setCooldown(cooldown);
      });

      socket.on('joinCooldownSet', (cooldown) => {
        setJoinCooldown(cooldown);
      });

      socket.on('callStarted', (newCallState) => {
        setCallState(newCallState);
      });

      socket.on('callEnded', () => {
        setCallState(null);
        setIsInCall(false);
      });

      return () => {
        socket.off('cooldownSet');
        socket.off('joinCooldownSet');
        socket.off('callStarted');
        socket.off('callEnded');
      };
    }
  }, [socket, setCallState]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    if (cooldown > 0) {
      const timer = setInterval(() => {
        setCooldown(prev => {
          if (prev <= 0) {
            clearInterval(timer);
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [cooldown]);

  useEffect(() => {
    if (joinCooldown > 0 && userJoinTime) {
      const timer = setInterval(() => {
        const timePassed = Math.floor((Date.now() - userJoinTime) / 1000);
        const remaining = Math.max(0, joinCooldown - timePassed);
        if (remaining <= 0) {
          clearInterval(timer);
        }
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [joinCooldown, userJoinTime]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      sendImage(file);
    }
  };

  const sendMessage = (e) => {
    e.preventDefault();
    if (!message.trim()) return;
    
    const isPrivileged = isAdmin || isSeniorMod || isModerator;
    
    if (!isPrivileged) {
      const timePassed = userJoinTime ? Math.floor((Date.now() - userJoinTime) / 1000) : 0;
      if (joinCooldown > 0 && timePassed < joinCooldown) {
        const remaining = joinCooldown - timePassed;
        if (setMessages) {
          setMessages(prev => [...prev, {
            id: Date.now(),
            type: 'system',
            content: `You must wait ${remaining}s before speaking...`,
            timestamp: new Date().toISOString()
          }]);
        }
        return;
      }

      if (cooldown > 0) {
        if (setMessages) {
          setMessages(prev => [...prev, {
            id: Date.now(),
            type: 'system',
            content: `You must wait ${cooldown}s before sending another message...`,
            timestamp: new Date().toISOString()
          }]);
        }
        return;
      }

      const containsLink = /https?:\/\/[^\s]+/.test(message);
      if (!(roomSettings?.allowLinks || mockRoomSettings.allowLinks) && containsLink) {
        if (setMessages) {
          setMessages(prev => [...prev, {
            id: Date.now(),
            type: 'system',
            content: 'Links are disabled in this realm...',
            timestamp: new Date().toISOString()
          }]);
        }
        return;
      }
    }

    const messageData = {
      id: Date.now(),
      username: username || 'Anonymous',
      content: message.trim(),
      timestamp: new Date().toISOString(),
      type: 'message'
    };

    if (socket && encrypt) {
      const encryptedMessage = encrypt(messageData);
      socket.emit('message', { roomId, message: encryptedMessage });
    }
    
    setMessage('');
    setIsTyping(false);
    if (!isPrivileged) {
      setCooldown(roomSettings?.cooldown || mockRoomSettings.cooldown);
    }
  };

  const sendImage = (file) => {
    const settings = roomSettings || mockRoomSettings;
    if (!settings.allowImages && !(isAdmin || isSeniorMod || isModerator)) {
      if (setMessages) {
        setMessages(prev => [...prev, {
          id: Date.now(),
          type: 'system',
          content: 'Image sharing is disabled in this realm...',
          timestamp: new Date().toISOString()
        }]);
      }
      return;
    }

    const reader = new FileReader();
    reader.onload = (e) => {
      const messageData = {
        id: Date.now(),
        username: username || 'Anonymous',
        content: e.target.result,
        timestamp: new Date().toISOString(),
        type: 'image'
      };

      if (socket && encrypt) {
        const encryptedMessage = encrypt(messageData);
        socket.emit('message', { roomId, message: encryptedMessage });
      }
      
      if (!(isAdmin || isSeniorMod || isModerator)) {
        setCooldown(roomSettings?.cooldown || mockRoomSettings.cooldown);
      }
    };
    reader.readAsDataURL(file);
  };

  const deleteMessage = (messageId) => {
    if (socket) {
      socket.emit('deleteMessage', { roomId, messageId });
    }
  };

  const copyRoomLink = () => {
    const link = `${window.location.origin}?room=${roomId}`;
    navigator.clipboard.writeText(link);
    if (setMessages) {
      setMessages(prev => [...prev, {
        id: Date.now(),
        type: 'system',
        content: 'Room link copied to clipboard!',
        timestamp: new Date().toISOString()
      }]);
    }
  };

  const startCall = () => {
    if (socket) {
      socket.emit('startCall', { roomId });
    }
    setIsInCall(true);
    setIsCallMinimized(false);
  };

  const joinCall = () => {
    if (socket) {
      socket.emit('joinCall', { roomId });
    }
    setIsInCall(true);
    setIsCallMinimized(false);
  };

  const minimizeCall = () => {
    setIsCallMinimized(true);
  };

  const timePassed = userJoinTime ? Math.floor((Date.now() - userJoinTime) / 1000) : 0;
  const joinCooldownRemaining = Math.max(0, joinCooldown - timePassed);

  const displayMessages = messages || mockMessages;
  const displayUsers = users || mockUsers;
  const displayRoomSettings = roomSettings || mockRoomSettings;

  return (
    <div className={`h-screen ${currentTheme.bg} flex flex-col transition-all duration-500 overflow-hidden`}>
      {/* Enhanced Header with Glassmorphism */}
      <div className={`${currentTheme.card} border-b ${currentTheme.border} p-6 shadow-2xl backdrop-blur-xl`}>
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <div className="relative">
              <Ghost className="w-10 h-10 text-purple-400 animate-pulse" />
              <div className="absolute -top-1 -right-1 w-4 h-4 bg-green-400 rounded-full animate-ping"></div>
            </div>
            <div>
              <h1 className="text-2xl font-bold bg-gradient-to-r from-purple-400 to-pink-400 bg-clip-text text-transparent tracking-tight">
                {roomName || 'Ethereal Realm'}
              </h1>
              <p className={`text-sm ${currentTheme.textSecondary} flex items-center space-x-2`}>
                <span>Room ID: {roomId || 'demo-room'}</span>
                <span>•</span>
                <span className="flex items-center space-x-1">
                  <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
                  <span>Online</span>
                </span>
              </p>
            </div>
          </div>
          
          <div className="flex items-center space-x-3">
            <div className={`${currentTheme.buttonSecondary} rounded-2xl px-4 py-3 flex items-center space-x-3 shadow-lg`}>
              <Users className="w-5 h-5 text-purple-400" />
              <span className={`text-sm font-medium ${currentTheme.textPrimary}`}>{displayUsers.length} souls</span>
            </div>
            
            <button
              onClick={copyRoomLink}
              className={`${currentTheme.buttonSecondary} rounded-2xl px-4 py-3 flex items-center space-x-2 ${currentTheme.hover} transition-all duration-300 shadow-lg hover:shadow-xl transform hover:scale-105`}
              title="Copy Room Link"
            >
              <Copy className="w-5 h-5" />
              <span className="hidden sm:inline font-medium">Copy</span>
            </button>

            <button
              onClick={() => setShowQRCode(true)}
              className={`${currentTheme.buttonSecondary} rounded-2xl px-4 py-3 flex items-center space-x-2 ${currentTheme.hover} transition-all duration-300 shadow-lg hover:shadow-xl transform hover:scale-105`}
              title="Share QR Code"
            >
              <Share2 className="w-5 h-5" />
              <span className="hidden sm:inline font-medium">Share</span>
            </button>
            
            {(isAdmin || isSeniorMod) && (
              <button
                onClick={() => setShowSettings(true)}
                className={`${currentTheme.buttonSecondary} rounded-2xl px-4 py-3 flex items-center space-x-2 ${currentTheme.hover} transition-all duration-300 shadow-lg hover:shadow-xl transform hover:scale-105`}
                title="Settings"
              >
                <Settings className="w-5 h-5" />
                <span className="hidden sm:inline font-medium">Settings</span>
              </button>
            )}

            {!callState && (
              <button
                onClick={startCall}
                className={`${currentTheme.button} rounded-2xl px-4 py-3 flex items-center space-x-2 transform hover:scale-105 transition-all duration-300 shadow-lg hover:shadow-xl`}
                title="Start Call"
              >
                <Phone className="w-5 h-5" />
                <span className="hidden sm:inline font-medium">Start Call</span>
              </button>
            )}

            {callState && !isInCall && (
              <button
                onClick={joinCall}
                className={`${currentTheme.button} rounded-2xl px-4 py-3 flex items-center space-x-2 transform hover:scale-105 transition-all duration-300 shadow-lg hover:shadow-xl animate-pulse`}
                title="Join Call"
              >
                <Phone className="w-5 h-5" />
                <span className="hidden sm:inline font-medium">Join Call</span>
              </button>
            )}
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex overflow-hidden">
        {/* Messages or Call Room */}
        <div className="flex-1 flex flex-col relative">
          {isInCall && callState && !isCallMinimized ? (
            <CallRoom
              socket={socket}
              roomId={roomId}
              username={username}
              users={displayUsers}
              callState={callState}
              onMinimize={minimizeCall}
            />
          ) : (
            <>
              {isInCall && (
                <div className={`${currentTheme.card} m-4 p-4 rounded-2xl flex justify-between items-center shadow-lg border ${currentTheme.border}`}>
                  <div className="flex items-center space-x-3">
                    <div className="w-3 h-3 bg-green-400 rounded-full animate-pulse"></div>
                    <Phone className="w-5 h-5 text-green-400" />
                    <span className={`text-sm font-medium ${currentTheme.textPrimary}`}>
                      In call with {callState?.participants?.length || 0} spirits
                    </span>
                  </div>
                  <button 
                    onClick={() => setIsCallMinimized(false)}
                    className={`text-sm text-purple-400 hover:text-purple-300 font-medium px-3 py-1 rounded-lg ${currentTheme.hover} transition-all duration-300`}
                  >
                    Show call
                  </button>
                </div>
              )}
              
              {/* Enhanced Messages Area */}
              <div className="flex-1 overflow-y-auto p-6 space-y-4 scrollbar-thin scrollbar-thumb-purple-600 scrollbar-track-transparent">
                {displayMessages.map((msg) => (
                  <Message
                    key={msg.id}
                    message={msg}
                    currentTheme={currentTheme}
                    isAdmin={isAdmin}
                    isSeniorMod={isSeniorMod}
                    isModerator={isModerator}
                    deleteMessage={deleteMessage}
                  />
                ))}
                <div ref={messagesEndRef} />
              </div>
              
              {/* Enhanced Message Input */}
              <div className={`border-t ${currentTheme.border} p-6 bg-gradient-to-r from-slate-800/50 via-purple-900/20 to-slate-800/50 backdrop-blur-xl`}>
                {joinCooldownRemaining > 0 && !(isAdmin || isSeniorMod || isModerator) && (
                  <div className="text-center text-sm text-purple-400 mb-4 flex items-center justify-center space-x-2 animate-pulse">
                    <Ghost className="w-4 h-4" />
                    <span>The spirits allow you to speak in {joinCooldownRemaining}s...</span>
                  </div>
                )}
                
                {isTyping && (
                  <div className="flex items-center space-x-2 mb-4 text-sm text-purple-400">
                    <div className="flex space-x-1">
                      <div className="w-2 h-2 bg-purple-400 rounded-full animate-bounce"></div>
                      <div className="w-2 h-2 bg-purple-400 rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div>
                      <div className="w-2 h-2 bg-purple-400 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
                    </div>
                    <span>Channeling your message...</span>
                  </div>
                )}
                
                <form onSubmit={sendMessage} className="flex space-x-4">
                  <input
                    type="file"
                    ref={fileInputRef}
                    onChange={handleFileUpload}
                    accept="image/*"
                    className="hidden"
                  />
                  
                  <button
                    type="button"
                    onClick={() => fileInputRef.current?.click()}
                    disabled={!displayRoomSettings.allowImages && !(isAdmin || isSeniorMod || isModerator)}
                    className={`${currentTheme.buttonSecondary} rounded-2xl p-4 flex items-center disabled:opacity-50 disabled:cursor-not-allowed ${currentTheme.hover} transition-all duration-300 shadow-lg hover:shadow-xl transform hover:scale-105`}
                    title="Share Image"
                  >
                    <Image className="w-5 h-5" />
                  </button>
                  
                  <div className="flex-1 relative">
                    <input
                      type="text"
                      value={message}
                      onChange={(e) => {
                        setMessage(e.target.value);
                        setIsTyping(e.target.value.length > 0);
                      }}
                      onBlur={() => setIsTyping(false)}
                      className={`${currentTheme.input} border rounded-2xl w-full px-6 py-4 focus:outline-none focus:ring-2 focus:ring-purple-500 transition-all duration-300 shadow-lg text-lg`}
                      placeholder={
                        cooldown > 0 && !(isAdmin || isSeniorMod || isModerator) 
                          ? `The spirits silence you for ${cooldown}s...` 
                          : "Whisper your secrets to the void..."
                      }
                      maxLength={500}
                      disabled={
                        (cooldown > 0 || 
                        (joinCooldown > 0 && joinCooldownRemaining > 0)) && 
                        !(isAdmin || isSeniorMod || isModerator)
                      }
                    />
                    {message && (
                      <div className="absolute right-3 top-1/2 transform -translate-y-1/2 text-xs text-slate-400">
                        {500 - message.length}
                      </div>
                    )}
                  </div>
                  
                  <button
                    type="submit"
                    disabled={
                      !message.trim() || 
                      (cooldown > 0 && !(isAdmin || isSeniorMod || isModerator)) || 
                      (joinCooldown > 0 && joinCooldownRemaining > 0 && !(isAdmin || isSeniorMod || isModerator))
                    }
                    className={`${currentTheme.button} rounded-2xl p-4 flex items-center disabled:opacity-50 disabled:cursor-not-allowed transform hover:scale-105 transition-all duration-300 shadow-lg hover:shadow-xl group`}
                    title="Send Message"
                  >
                    <Send className="w-5 h-5 group-hover:translate-x-1 transition-transform duration-300" />
                  </button>
                </form>
                
                {/* Cooldown indicator */}
                {cooldown > 0 && !(isAdmin || isSeniorMod || isModerator) && (
                  <div className="mt-4 flex items-center justify-center space-x-2 text-sm text-purple-400">
                    <div className="w-full bg-slate-700 rounded-full h-2 overflow-hidden">
                      <div 
                        className="h-full bg-gradient-to-r from-purple-500 to-pink-500 rounded-full transition-all duration-1000"
                        style={{ width: `${((displayRoomSettings.cooldown - cooldown) / displayRoomSettings.cooldown) * 100}%` }}
                      ></div>
                    </div>
                    <span className="whitespace-nowrap">{cooldown}s</span>
                  </div>
                )}
              </div>
            </>
          )}
        </div>
        
        {/* Enhanced User List */}
        <UserList
          users={displayUsers}
          isAdmin={isAdmin}
          isSeniorMod={isSeniorMod}
          isModerator={isModerator}
          roomId={roomId}
          socket={socket}
          currentTheme={currentTheme}
          resetState={resetState}
        />
      </div>

      {/* Enhanced Modals */}
      <SettingsModal
        showSettings={showSettings}
        setShowSettings={setShowSettings}
        users={displayUsers}
        isAdmin={isAdmin}
        isSeniorMod={isSeniorMod}
        roomSettings={displayRoomSettings}
        setRoomSettings={setRoomSettings}
        socket={socket}
        roomId={roomId}
        currentTheme={currentTheme}
        resetState={resetState}
      />

      <QRCodeModal
        showQRCode={showQRCode}
        setShowQRCode={setShowQRCode}
        roomId={roomId}
        currentTheme={currentTheme}
      />

      {/* Floating particles effect */}
      <div className="fixed inset-0 pointer-events-none overflow-hidden">
        {[...Array(20)].map((_, i) => (
          <div
            key={i}
            className="absolute w-2 h-2 bg-purple-400/20 rounded-full animate-float"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 10}s`,
              animationDuration: `${10 + Math.random() * 20}s`
            }}
          />
        ))}
      </div>

      {/* Custom styles for animations */}
      <style jsx>{`
        @keyframes float {
          0%, 100% {
            transform: translateY(0px) rotate(0deg);
            opacity: 0;
          }
          50% {
            transform: translateY(-20px) rotate(180deg);
            opacity: 1;
          }
        }
        
        .animate-float {
          animation: float linear infinite;
        }
        
        .scrollbar-thin::-webkit-scrollbar {
          width: 6px;
        }
        
        .scrollbar-thin::-webkit-scrollbar-track {
          background: transparent;
        }
        
        .scrollbar-thin::-webkit-scrollbar-thumb {
          background: rgba(147, 51, 234, 0.3);
          border-radius: 3px;
        }
        
        .scrollbar-thin::-webkit-scrollbar-thumb:hover {
          background: rgba(147, 51, 234, 0.5);
        }
        
        .gradient-text {
          background: linear-gradient(45deg, #8b5cf6, #ec4899);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
        }
        
        .animate-pulse-slow {
          animation: pulse 3s cubic-bezier(0.4, 0, 0.6, 1) infinite;
        }
      `}</style>
    </div>
  );
};

export default Chat;
